INSERT INTO `article_description` VALUES ('65', 'en', 'Developing derivative securities market and some note for Vietnam', '<p style=\"text-align: justify;\">\r\n	(T&agrave;i ch&iacute;nh) Thị trường chứng kho&aacute;n (TTCK) ph&aacute;i sinh l&agrave; cấp độ ph&aacute;t triển cao nhất của TTCK. X&acirc;y dựng TTCK ph&aacute;i sinh l&agrave; điều cần thiết, l&agrave; xu thế ph&aacute;t triển tất yếu nhằm ho&agrave;n thiện thị trường vốn Việt Nam, qua đ&oacute; gi&uacute;p đẩy mạnh qu&aacute; tr&igrave;nh huy động v&agrave; khơi th&ocirc;ng c&aacute;c nguồn vốn cho đầu tư ph&aacute;t triển kinh tế, văn h&oacute;a, x&atilde; hội.</p>\r\n', '<p style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.2000007629395px; text-align: justify;\">TTCK ph&aacute;i sinh l&agrave; một định chế t&agrave;i ch&iacute;nh bậc cao chuy&ecirc;n giao dịch c&aacute;c chứng kho&aacute;n ph&aacute;i sinh, bao gồm hợp đồng kỳ hạn (forward contracts), hợp đồng tương lai (future contracts), hợp đồng quyền chọn (options) v&agrave; ho&aacute;n đổi (swaps), được ph&aacute;t h&agrave;nh dựa tr&ecirc;n cơ sở những c&ocirc;ng cụ t&agrave;i ch&iacute;nh đ&atilde; c&oacute; sẵn tr&ecirc;n TTCK như cố phiếu hay c&aacute;c chỉ số chứng kho&aacute;n. TTCK ph&aacute;i sinh c&oacute; nguồn gốc từ thị trường h&agrave;ng h&oacute;a, tuy nhi&ecirc;n, việc x&acirc;y dựng v&agrave; ph&aacute;t triển TTCK ph&aacute;i sinh lại phức tạp hơn nhiều thị trường h&agrave;ng h&oacute;a do quy m&ocirc; rộng lớn cũng như t&iacute;nh đa dạng trong c&aacute;c sản phẩm của TTCK ph&aacute;i sinh.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', '', '');
INSERT INTO `article_description` VALUES ('50', 'vn', 'Về chúng tôi', '<p>\r\n	<strong style=\"font-size: 12.222222328186px; text-align: justify;\">Intelligent Financial Research &amp; Consulting (IFRC)&nbsp;</strong><span style=\"font-size: 12.222222328186px; text-align: justify;\">l&agrave; c&ocirc;ng ty đa dịch vụ. Sản phẩm v&agrave; dịch vụ trọng yếu của IFRC bao gồm Sản phẩm v&agrave; Dịch vụ t&agrave;i ch&iacute;nh cho Thị trường vốn, Nghi&ecirc;n cứu, Đ&agrave;o tạo, Tư vấn quản l&yacute; v&agrave; dịch vụ IT. Chỉ số t&ugrave;y chỉnh, được x&acirc;y dựng bởi đội ngũ x&acirc;y dựng chỉ số chuy&ecirc;n nghiệp, l&agrave; sản phẩm chủ chốt của ch&uacute;ng t&ocirc;i.</span></p>\r\n', '<div style=\"text-align: justify;\">\r\n	<strong>Intelligent Financial Research &amp; Consulting (IFRC)&nbsp;</strong>l&agrave; c&ocirc;ng ty đa dịch vụ. Sản phẩm v&agrave; dịch vụ trọng yếu của IFRC bao gồm Sản phẩm v&agrave; Dịch vụ t&agrave;i ch&iacute;nh cho Thị trường vốn, Nghi&ecirc;n cứu, Đ&agrave;o tạo, Tư vấn quản l&yacute; v&agrave; dịch vụ IT. Chỉ số t&ugrave;y chỉnh, được x&acirc;y dựng bởi đội ngũ x&acirc;y dựng chỉ số chuy&ecirc;n nghiệp, l&agrave; sản phẩm chủ chốt của ch&uacute;ng t&ocirc;i.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div style=\"text-align: justify;\">\r\n	<strong>Ch&uacute;ng t&ocirc;i cam kết cung cấp c&aacute;c dịch vụ v&agrave; s&agrave;n phẩm tốt nhất trong suốt qua tr&igrave;nh x&acirc;y dựng v&agrave; duy tr&igrave; chỉ số: khai th&aacute;c dữ liệu, thiết kế, ph&aacute;t triển, c&ocirc;ng bố, duy tr&igrave;, hỗ trợ, gi&aacute;o dục v&agrave; nghi&ecirc;n cứu.</strong></div>\r\n', '', '');
INSERT INTO `article_description` VALUES ('51', 'en', 'IFRC VNX Indexes', '<p>\r\n	<strong>IFRC Index has developed a complete range of Vietnamese indexes.&nbsp; </strong></p>\r\n<p>\r\n	This family comprises of 400 representative indices, including Benchmark, Size, Sectors, Blue Chips, Strategy and Theme indexes, calculated in Price and Total return versions and converted into 4 different major currencies: VND, USD, EUR and JPY They are historically reconstructed since 2008.</p>\r\n', '', '', '');
