INSERT INTO `article_description` VALUES ('61', 'en', 'Development derivatives markets and a few notes for Vietnam', '<p style=\"text-align: justify;\">\r\n	(T&agrave;i ch&iacute;nh) Việc x&acirc;y dựng thị trường chứng kho&aacute;n ph&aacute;i sinh (TTCKPS) l&agrave; một mục ti&ecirc;u quan trọng trong chiến lược ph&aacute;t triển thị trường chứng kho&aacute;n Việt Nam 2011-2020 đ&atilde; được Thủ tướng ph&ecirc; duyệt theo quyết định 252/2012/QĐ-TTg</p>\r\n', '<p style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.2000007629395px; text-align: justify;\">Mới đ&acirc;y nhất, ng&agrave;y 11/3/2014, Thủ tướng Ch&iacute;nh phủ đ&atilde; k&yacute; Quyết định số 366/QĐ-TTg ph&ecirc; duyệt Đề &aacute;n x&acirc;y dựng v&agrave; ph&aacute;t triển thị trường chứng kho&aacute;n ph&aacute;i sinh Việt Nam. Đ&acirc;y được xem l&agrave; những bước đi đầu ti&ecirc;n trong việc x&acirc;y dựng v&agrave; ph&aacute;t triển TTCKPS tại Việt Nam nhằm đ&aacute;p ứng nhu cầu cấp thiết của cơ chế ph&ograve;ng vệ rủi ro.</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.2000007629395px; text-align: justify;\">Tuy nhi&ecirc;n, TTCKPS cũng được xem l&agrave; một thị trường c&oacute; hiệu ứng đ&ograve;n bẩy t&agrave;i ch&iacute;nh cao, do đ&oacute; việc x&acirc;y dựng v&agrave; ph&aacute;t triển thị trường n&agrave;y cũng hết sức nhạy cảm, phức tạp đ&ograve;i hỏi phải c&oacute; sự chuẩn bị kỹ lưỡng từ ph&iacute;a cơ quan quản l&yacute; nh&agrave; nước cũng như sự chuẩn bị sẵn s&agrave;ng của c&aacute;c th&agrave;nh vi&ecirc;n thị trường.</span></p>\r\n', '', '');
INSERT INTO `article_description` VALUES ('60', 'vn', 'Sao phải cần thị trường chứng khoán phái sinh?', '<p>\r\n	(T&agrave;i ch&iacute;nh) Đến năm 2016, sẽ bắt đầu tổ chức giao dịch c&aacute;c chứng kho&aacute;n ph&aacute;i sinh dựa tr&ecirc;n t&agrave;i sản cơ sở l&agrave; chỉ số chứng kho&aacute;n, tr&aacute;i phiếu, cổ phiếu</p>\r\n', '<p>\r\n	Ng&agrave;y 11/3/2014, Thủ tướng Ch&iacute;nh phủ Nguyễn Tấn Dũng đ&atilde; k&yacute; Quyết định ph&ecirc; duyệt Đề &aacute;n X&acirc;y dựng v&agrave; ph&aacute;t triển thị trường chứng kho&aacute;n (TTCK) ph&aacute;i sinh Việt Nam. Đề &aacute;n mở rộng kh&ocirc;ng gian ph&aacute;t triển TTCK Việt Nam ra khỏi phạm vi c&aacute;c sản phẩm cơ sở v&agrave; x&aacute;c lập lộ tr&igrave;nh đến năm 2016, sẽ bắt đầu tổ chức giao dịch c&aacute;c chứng kho&aacute;n ph&aacute;i sinh dựa tr&ecirc;n t&agrave;i sản cơ sở l&agrave; chỉ số chứng kho&aacute;n, tr&aacute;i phiếu, cổ phiếu.</p>\r\n', '', '');
