INSERT INTO `article_description` VALUES ('62', 'en', 'Derivatives securities market has official schedule of development', '<p>\r\n	(T&agrave;i ch&iacute;nh) Ng&agrave;y 11/3/2014, Thủ tướng Ch&iacute;nh phủ đ&atilde; ph&ecirc; duyệt Đề &aacute;n x&acirc;y dựng v&agrave; ph&aacute;t triển thị trường chứng kho&aacute;n (TTCK) ph&aacute;i sinh Việt Nam.</p>\r\n', '<p>\r\n	Kh&ocirc;ng để TTCK ph&aacute;i sinh h&igrave;nh th&agrave;nh v&agrave; hoạt động tự ph&aacute;t</p>\r\n<p style=\"text-align: justify;\">\r\n	Theo Quyết định tr&ecirc;n, quan điểm v&agrave; nguy&ecirc;n tắc x&acirc;y dựng v&agrave; ph&aacute;t triển TTCK ph&aacute;i sinh được dựa tr&ecirc;n quan điểm v&agrave; nguy&ecirc;n tắc: Dựa tr&ecirc;n c&aacute;c ti&ecirc;u chuẩn v&agrave; th&ocirc;ng lệ quốc tế, ph&ugrave; hợp với điều kiện thực tế v&agrave; ph&aacute;t triển kinh tế- x&atilde; hội của đất nước; Theo m&ocirc; h&igrave;nh tập trung, hoạt động thống nhất dưới sự quản l&yacute; của Nh&agrave; nước, kh&ocirc;ng để TTCK ph&aacute;i sinh h&igrave;nh th&agrave;nh v&agrave; hoạt động tự ph&aacute;t; Lựa chọn v&agrave; chuẩn h&oacute;a c&aacute;c chứng kho&aacute;n ph&aacute;i sinh giao dịch tr&ecirc;n Sở giao dịch chứng kho&aacute;n l&agrave; c&aacute;c hợp đồng tương lai, hợp đồng quyền chọn dựa tr&ecirc;n c&aacute;c chứng kho&aacute;n cơ sở l&agrave; chỉ số TTCK, tr&aacute;i phiếu ch&iacute;nh phủ, cổ phiếu ni&ecirc;m yết.</p>\r\n', '', '');
