INSERT INTO `article_description` VALUES ('66', 'en', 'Experience in building derivatives securities market for Vietnam', '<p style=\"text-align: justify;\">\r\n	(T&agrave;i ch&iacute;nh) Việc nghi&ecirc;n cứu kinh nghiệm sử dụng c&aacute;c sản phẩm chứng kho&aacute;n ph&aacute;i sinh ở c&aacute;c ng&acirc;n h&agrave;ng hay c&aacute;c tổ chức t&agrave;i ch&iacute;nh tại Australia l&agrave; rất cần thiết đối với qu&aacute; tr&igrave;nh n&acirc;ng cao hiệu quả quản trị rủi ro tại c&aacute;c ng&acirc;n h&agrave;ng thương mại (NHTM) Việt Nam hiện nay n&oacute;i ri&ecirc;ng v&agrave; l&agrave; b&agrave;i học kinh nghiệm quan trọng trong việc x&acirc;y dựng thị trường chứng kho&aacute;n ph&aacute;i sinh ở Việt Nam trong thời gian tới.</p>\r\n', '<p style=\"text-align: justify;\">\r\n	Từ l&acirc;u, c&aacute;c NHTM tr&ecirc;n thế giới đ&atilde; sử dụng c&aacute;c sản phẩm chứng kho&aacute;n ph&aacute;i sinh nhằm n&acirc;ng cao hiệu quả quản trị rủi ro trong hoạt động kinh doanh ng&acirc;n h&agrave;ng. Tại Australia, bốn NHTM lớn nhất nước n&agrave;y l&agrave; Australia and New Zealand Bank Group (ANZ), Commonwealth Bank of Australia (CBA), National Austrlia Bank (NAB) v&agrave; Westpac Banking Corporation (WBK) đ&atilde; sử dụng ng&agrave;y c&agrave;ng nhiều c&aacute;c sản phẩm chứng kho&aacute;n ph&aacute;i sinh. Việc l&agrave;m n&agrave;y đ&atilde; mang lại kh&ocirc;ng chỉ hiệu quả giảm thiểu rủi ro m&agrave; c&ograve;n tăng th&ecirc;m lợi nhuận cho ng&acirc;n h&agrave;ng.</p>\r\n<p>\r\n	&nbsp;</p>\r\n', '', '');
