INSERT INTO `article_description` VALUES ('68', 'en', 'Development of global derivatives market ', '<p style=\"text-align: justify;\">\r\n	(T&agrave;i ch&iacute;nh) V&agrave;o cuối thế kỷ 20, tr&agrave;o lưu đổi mới t&agrave;i ch&iacute;nh mạnh mẽ đ&atilde; h&igrave;nh th&agrave;nh n&ecirc;n một khu vực thị trường t&agrave;i ch&iacute;nh với quy m&ocirc; rộng khắp tr&ecirc;n to&agrave;n cầu, ph&aacute;t triển với tốc độ rất cao, đ&oacute; l&agrave; thị trường c&aacute;c c&ocirc;ng cụ t&agrave;i ch&iacute;nh ph&aacute;i sinh (c&aacute;c hợp đồng t&agrave;i ch&iacute;nh).</p>\r\n', '<p style=\"text-align: justify;\">\r\n	C&ugrave;ng với c&aacute;c c&ocirc;ng cụ t&agrave;i ch&iacute;nh truyền thống (cổ phiếu, tr&aacute;i phiếu,...), đ&atilde; xuất hiện c&aacute;c c&ocirc;ng cụ t&agrave;i ch&iacute;nh mới như hợp đồng kỳ hạn, hợp đồng tương lai, hợp đồng quyền chọn v&agrave; hợp đồng ho&aacute;n đổi. C&aacute;c c&ocirc;ng cụ t&agrave;i ch&iacute;nh ph&aacute;i sinh dựa tr&ecirc;n c&aacute;c quan hệ t&agrave;i ch&iacute;nh cơ sở (t&iacute;n dụng, tr&aacute;i phiếu, cổ phiếu,...), th&ocirc;ng qua việc sử dụng c&aacute;c thỏa thuận mới về nội dung, g&oacute;p phần bổ sung v&agrave; ph&aacute;t triển ch&uacute;ng. Hiện nay, việc thiết kế, đưa v&agrave;o &aacute;p dụng v&agrave; tổ chức lưu th&ocirc;ng c&aacute;c c&ocirc;ng cụ t&agrave;i ch&iacute;nh ph&aacute;i sinh l&agrave; một lĩnh vực độc lập trong hoạt động t&agrave;i ch&iacute;nh.</p>\r\n', '', '');
