INSERT INTO `article_description` VALUES ('93', 'en', 'Arbitrage', '<p>\r\n	Attempting to profit by exploiting price references of identical or similar commodities or financial instruments on different markets or in different forms. Arbitrage opportunities (If they exist) provide riskless profit opportunities.</p>\r\n', '', '', '');
