INSERT INTO `article_description` VALUES ('96', 'en', 'Calendar spread', '<p>\r\n	An option transaction consisting of the purchase of an option with a given expiration an the sale of an otherwise identical option with a different expiration.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('96', 'vn', 'Mua và bán song hành theo lịch', '<p style=\"text-align: justify;\">\r\n	S&aacute;ch l&shy;ược mua b&aacute;n hợp đồng options bằng c&aacute;ch mua 2 hợp đồng option c&oacute; c&ugrave;ng loại chứng kho&aacute;n nh&shy;ưng kh&aacute;c ng&agrave;y đ&aacute;o hạn. Nếu gi&aacute; điểm (exercise price= strike price = gi&aacute; thi h&agrave;nh) giống nhau th&igrave; gọi l&agrave; ch&ecirc;nh lệch theo chiều ngang (horizontal spread). Nếu gi&aacute; thi h&agrave;nh kh&aacute;c nhau th&igrave; gọi l&agrave; ch&ecirc;nh lệch theo đ&shy;ờng ch&eacute;o (diagonal spread). Nh&agrave; đầu tư&shy; lời hay lỗ l&agrave; do ch&ecirc;nh lệch gi&aacute; hẹp hay rộng, &iacute;t hay nhiều.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('97', 'en', 'Call Option', '<p>\r\n	A call option gives the buyer of the option the right but not the obligation to buy the underlying futures contract at a specified price.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('97', 'vn', 'Quyền chọn Mua', '<p>\r\n	Một loại hợp đồng t&agrave;i ch&iacute;nh diễn ra giữa hai b&ecirc;n, cho ph&eacute;p người mua hợp đồng c&oacute; quyền mua một số lượng x&aacute;c định cổ phần của một chứng kho&aacute;n, chỉ số chứng kho&aacute;n n&agrave;o đ&oacute; ở một mức gi&aacute; ấn định trước, gọi l&agrave; &quot;strike price&quot; hoặc &quot;exercise price&quot; - v&agrave;o hoặc trước ng&agrave;y đ&aacute;o hạn của hợp đồng.</p>\r\n', '', '', '');
