INSERT INTO `article_description` VALUES ('106', 'en', 'Initial Margin', '<p style=\"text-align: justify;\">\r\n	The minimum amount of money that must be in and investment account on the day of a transaction. On futures accounts, the initial margin must be met on any day in which the opening balance starts off below the maintenance margin requirement.</p>\r\n', '', '', '');
