INSERT INTO `article_description` VALUES ('99', 'vn', 'Tổ chức thanh toán bù trừ', '<p>\r\n	Tổ chức t&agrave;i ch&iacute;nh đảm bảo cho tất cả c&aacute;c hợp đồng giao dịch tr&ecirc;n Sở giao dịch v&agrave; quản l&yacute; thanh to&aacute;n b&ugrave; trừ cho c&aacute;c hợp đồng tương lai v&agrave; quyền chọn.</p>\r\n', '', '', '');
