INSERT INTO `article_description` VALUES ('101', 'en', 'Contract Specification', '<p style=\"text-align: justify;\">\r\n	A derivative exchange designs its own products and publishes a contract specification setting out the details of the derivative contract. This will include the size or unit of trading and the underlying, maturity months, quotation and minimum price movement and value together with trading times, methods and delivery condition.</p>\r\n', '', '', '');
