INSERT INTO `article_description` VALUES ('102', 'en', 'Counterparty', '<p style=\"text-align: justify;\">\r\n	A trade can take place between two or more counterparties. Usually one party to a trade refers to its trading partners as counterparties.</p>\r\n', '', '', '');
