INSERT INTO `article_description` VALUES ('104', 'en', 'Forward Contract', '<p style=\"text-align: justify;\">\r\n	An agreement between two parties, a buyer and seller, to buy an asset or currency at a later date at a fix price.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('104', 'vn', 'Hợp đồng kỳ hạn', '<p style=\"text-align: justify;\">\r\n	Cam kết giữa hai b&ecirc;n, b&ecirc;n mua v&agrave; b&ecirc;n b&aacute;n để mua một t&agrave;i sản hoặc ngoại tệ v&agrave;o một ng&agrave;y x&aacute;c định trong tương lai với mức gi&aacute; được thoả thuận khi k&yacute; hợp đồng.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('105', 'en', 'Futures Contract', '<p style=\"text-align: justify;\">\r\n	An agreement between two parties, a buyer and a seller, to purchase an asset or currency at a later date at a fix price and that trades on a futures exchange and is subject to a daily settlement procedure to guarantee to each party that claims against the other parties will be paid.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('105', 'vn', 'Hợp đồng tương lai', '<p>\r\n	Thoả thuận giữa hai b&ecirc;n ( mua v&agrave; b&aacute;n) mua một t&agrave;i sản hay ngoại tệ được chuẩn ho&aacute; tại mức gi&aacute; được x&aacute;c định ng&agrave;y h&ocirc;m nay v&agrave; giao h&agrave;ng v&agrave;o thời điểm x&aacute;c định trong tương lai, giao dịch tr&ecirc;n S&agrave;n giao dịch tương lai, được đảm bảo thanh to&aacute;n l&atilde;i/ lỗ hằng ng&agrave;y tr&ecirc;n t&agrave;i khoản k&yacute; quỹ nhằm đảm bảo t&iacute;nh thanh khoản cho c&aacute;c b&ecirc;n.</p>\r\n', '', '', '');
