INSERT INTO `article_description` VALUES ('108', 'en', 'Leverage', '<p style=\"text-align: justify;\">\r\n	The magnification of gains and losses by paying only for part of the underlying value of the instrument or asset; the smaller the amount of funds invested, the greater the leverage. It is also known as gearing.</p>\r\n', '', '', '');
