INSERT INTO `article_description` VALUES ('109', 'en', 'Margin Call', '<p style=\"text-align: justify;\">\r\n	Where the brokerage calls the account holder in order for them to pay more funds into their account to maintain the trade.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('109', 'vn', 'Lệnh gọi', '<p>\r\n	Trường hợp m&ocirc;i giới gọi cho chủ t&agrave;i khoản để họ trả tiền v&agrave;o t&agrave;i khoản của họ để duy tr&igrave; c&aacute;c giao dịch.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('110', 'en', 'Market Maker', '<p style=\"text-align: justify;\">\r\n	A trader or trading firm that buys and sells securities in a market in order to facilitate trading. Market makers make a two side market.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('110', 'vn', 'Nhà tạo lập thị trường', '<p>\r\n	<span style=\"color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 20px;\">C&aacute;c c&aacute; nh&acirc;n hoặc tổ chức đứng ra tự giao dịch một hay một số loại c&ocirc;ng cụ t&agrave;i ch&iacute;nh. C&aacute;ch họ giao dịch l&agrave; tự m&igrave;nh định ra mức gi&aacute; mua v&agrave;o v&agrave; b&aacute;n ra của những loại t&agrave;i sản, h&agrave;ng h&oacute;a trong danh mục kinh doanh của họ. Mức ch&ecirc;nh lệch gi&aacute; mua gi&aacute; b&aacute;n v&agrave; những biến động gi&aacute;&nbsp;l&agrave; phương tiện chủ yếu để market-maker t&igrave;m kiếm lợi nhuận.</span></p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('111', 'en', 'Open Interest', '<p>\r\n	The number of futures or options contracts that have been established and not yet been offset or exercised.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('111', 'vn', 'Cột đang giao dịch', '<p>\r\n	Thể hiện tổng số lượng hợp đồng tương lai hoặc quyền chọn đang giao dịch.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('112', 'en', 'Position Limit', '<p>\r\n	The maximum number of options or futures contracts that any one investor can hold.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('112', 'vn', 'Hạn mức vị thế', '<p>\r\n	Số lượng hợp đồng tương lai v&agrave; quyền chọn tối đa m&agrave; nh&agrave; đầu tư đang nắm giữ.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('113', 'en', 'Settlement price', '<p style=\"text-align:left;\">The official price established by the clearinghouse at the end of each day for use in the daily settlement.</p>\r\n', '', '', '');
