INSERT INTO `article_description` VALUES ('244', 'en', 'Analysis on risk management, derivatives and regulation', '<p>\r\n	<strong>Daily Financial Risk Management News &amp; Analysis </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	Risk.net is your online gateway to our range of premium content covering the international financial risk management sector.</p>\r\n<p style=\"text-align: justify;\">\r\n	50+ Additional News Articles Available Online Every Month</p>\r\n<p style=\"text-align: justify;\">\r\n	Over fifty additional news articles from Risk.net brands including Asia Risk, Energy Risk, Insurance Risk, Operational Risk &amp; Regulation, Risk and Structured Products on the fast-moving worlds of complex and sophisticated finance, regulation, derivatives, and risk management are available exclusively online at Risk.net. &raquo; Risk.net News</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('244', 'vn', '', '', '', '', '');
INSERT INTO `article_description` VALUES ('245', 'en', 'International Journal of Financial Markets and Derivatives', '<p>\r\n	Editor in Chief: Dr. Christos Floros</p>\r\n<p>\r\n	ISSN online: 1756-7149</p>\r\n<p>\r\n	ISSN print: 1756-7130 4 issues per year</p>\r\n<p style=\"text-align: justify;\">\r\n	<em>IJFMD</em> addresses the advancement of contemporary research in the field of financial markets and derivatives. It is an internationally competitive, peer-reviewed journal dedicated to serve as the primary outlet for theoretical and empirical research in all areas of international markets and derivatives.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('245', 'vn', '', '', '', '', '');
INSERT INTO `article_description` VALUES ('247', 'en', 'Derivatives Quarterly', '<p style=\"text-align: justify;\">\r\n	<strong>Derivatives Quarterly</strong> educates the financial professional on derivatives, explaining how and why they are used, defining the risks and benefits, and providing practical strategies and real-life applications. It is the perfect derivatives guide to successful risk management and performance enhancement for all financial professionals, including treasury and asset/liability specialists, CFOs, corporate risk and money managers, traders, sales people, investment and pension fund managers, consultants, research analysts, and commercial bankers.</p>\r\n', '', '', '');
