INSERT INTO `article_description` VALUES ('249', 'en', 'An Introduction to the Mathematics of Financial Derivatives, Second Edition (Academic Press Advanced Finance)', '<p>\r\n	by <strong>Salih N. Neftci </strong></p>\r\n<p>\r\n	<em>Hardcover &ndash; June 2, 2000 </em></p>\r\n<p style=\"text-align: justify;\">\r\n	This popular text, publishing Spring 1999 in its Second Edition, introduces the mathematics underlying the pricing of derivatives. The increase of interest in dynamic pricing models stems from their applicability to practical situations: with the freeing of exchange, interest rates, and capital controls, the market for derivative products has matured and pricing models have become more accurate.</p>\r\n', '<p style=\"text-align: justify;\">\r\n	Professor Neftci&#39;s book answers the need for a resource targeting professionals, Ph.D. students, and advanced MBA students who are specifically interested in these financial products. The Second Edition is designed to make the book the main text in first year masters and Ph.D. programs for certain courses, and will continue to be an important manual for market professionals.</p>\r\n', '', '');
INSERT INTO `article_description` VALUES ('249', 'vn', '', '', '', '', '');
INSERT INTO `article_description` VALUES ('250', 'en', 'Market Data Express', '<p>\r\n	Market Data Express (<abbr title=\"Market Data Express\">MDX</abbr>) gives users quick and easy access, in some cases to more than 20 years of various historical market data. MDX offers a wide scope of market data, ranging from end-of-day summaries and comprehensive CBOE &amp; CFE Time-and-Sales reports to sorted, user-friendly OPRA Time-and-Sales data. Finally, anyone who needs historical market data, from quantitative analysts to academics can find everything they need in one place: Market Data Express.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('250', 'vn', '', '', '', '', '');
INSERT INTO `article_description` VALUES ('251', 'en', 'CBOE - Historical Data', '<p>\r\n	Inclused:</p>\r\n<ul>\r\n	<li>\r\n		Historical Daily Prices - Spreadsheet with Closing Prices for Several Indexes</li>\r\n	<li>\r\n		Historical Month - end Prices - Spreadsheet with Closing Prices for Several Indexes</li>\r\n	<li>\r\n		market Data Express gives users access to more than 16 years of historical options data</li>\r\n	<li>\r\n		Daily Volume and Put/Call Ratios</li>\r\n</ul>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('251', 'vn', '', '', '', '', '');
INSERT INTO `article_description` VALUES ('252', 'en', 'Tick Data', '<p>\r\n	For over 30 years, the world&rsquo;s largest investment banks, asset managers, proprietary traders and universities have relied upon&nbsp;our historical intraday data to back-test trading strategies, develop risk &amp; execution models, perform post-trade analysis, and conduct important academic research. Owned by its managers, Tick Data is passionately focused on data quality. And with data as far back as 1974, our offering is one of the largest of its kind.</p>\r\n', '', '', '');
