INSERT INTO `article_description` VALUES ('334', 'en', 'Implied Volatility', '<p>\r\n	In financial mathematics, the implied volatility of an option contract is that value of the volatility of the underlying instrument which, when input in an option pricing model will return a theoretical value equal to the current market price of the option</p>\r\n', '', '', '');
