INSERT INTO `article_description` VALUES ('353', 'en', 'Diep HO', '<p>\r\n	<span style=\"font-size:12px;\"><strong>Nationality: </strong>British</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Director and Founder at Zomia Group; Entrepreneur and Lecturer at HCMC International University</span></strong></p>\r\n<p>\r\n	<strong>10 years total capital market experience</strong></p>\r\n<ul>\r\n	<li>\r\n		2 years as a consultant for the Financial and Real Estate sectors.</li>\r\n	<li>\r\n		1.5 years in a dual role as Chief Investment Strategist and Chief Financial Strategist in Vietnam.</li>\r\n	<li>\r\n		1 year experience as the Senior Risk Developer, developing new risk model for hedge-fund strategies...</li>\r\n	<li>\r\n		2 years in a dual role as Chief Risk Officer (CRO) and Chief Operation Officer (COO), structuring risk and setup operation at a de novo Austrian (Basel II) regulated bank&mdash;Azure AG&mdash;with US$10 million startup investment from Cheyne Capital Management.</li>\r\n</ul>\r\n<p>\r\n	<a href=\"http://vn.linkedin.com/pub/diep-ho/5/866/401\" target=\"_blank\"><img class=\"thumbnails\" src=\"http://local.itvn.fr/test/vnvdm_demo/assets/upload/images/logo/unnamed.png\" style=\"width: 25px; height: 27px;\" /></a>&nbsp;&nbsp; <a href=\"http://local.itvn.fr/vnvdm/assets/upload/PDF/IU_DIEP_CV_EN.doc\" target=\"_blank\"><img class=\"thumbnails\" src=\"http://local.itvn.fr/test/vnvdm_demo/assets/upload/images/logo/CV.png\" /></a>&nbsp;&nbsp;&nbsp;<a href=\"http://www.diepho.info/\" target=\"_blank\"><img class=\"thumbnails\" src=\"http://local.itvn.fr/test/vnvdm_demo/assets/upload/images/logo/www.png\" style=\"width: 25px; height: 27px;\" /></a></p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('354', 'en', 'Huu Minh MAI', '<p>\r\n	<span style=\"font-size:12px;\"><strong>Nationality: </strong>French</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">CEO &amp; Founder of Intelligent Financial Research and Consulting (IFRC) and Lecturer at Paris XII University (France)</span></strong></p>\r\n<p>\r\n	<span style=\"font-weight: bold;\">Profile</span>: PhD in Finance (University Paris Dauphine) and Master Degree of Applied Mathematics (Paris Dauphine - ENSAE)<br />\r\n	<span style=\"font-weight: bold;\">Experiences</span>: Double competencies. More than 15 years in the Euronext Liffe, Paris Stock Exchange, Euronext and New York Stock Exchange. Lecturer in universities and business schools<br />\r\n	<span style=\"font-weight: bold;\">Specialities</span>: Financial Markets, Portfolio Management, Derivatives Market and Products, Indexes Design, Calculation and Management, Financial Mathematics, Empirical Research in Finance, etc.</p>\r\n<p>\r\n	<a href=\"https://www.linkedin.com/in/huuminhmai\" target=\"_blank\"><img class=\"thumbnails\" src=\"http://local.itvn.fr/test/vnvdm_demo/assets/upload/images/logo/unnamed.png\" style=\"width: 25px; height: 27px;\" /></a>&nbsp;&nbsp;&nbsp; <a href=\"http://local.itvn.fr/vnvdm/assets/upload/PDF/CV_HMM_GB_20150210.pdf\" target=\"_blank\"><img class=\"thumbnails\" src=\"http://local.itvn.fr/test/vnvdm_demo/assets/upload/images/logo/CV.png\" /></a></p>\r\n', '', '', '');
