INSERT INTO `article_description` VALUES ('357', 'en', 'William PERRAUDIN', '<p>\r\n	<span style=\"font-size:12px;\"><strong>Nationality</strong>: British</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Imperial College Adjunct Professor and Director of Risk Control Ltd.</span></strong></p>\r\n<p>\r\n	<span style=\"font-weight: bold;\">Experience</span>: more than 8 years teaching finance in college and more than 15 years as director of Risk Control ltd. with about 69 research publications. Professor Perraudin previously taught at the University of Cambridge, Imperial College and Birkbeck College and have worked at the International Monetary Fund and as a City stockbroker. He was a Special Advisor to the Bank of England and as such was involved in the parameterisation of the Basel II capital charges for international banks.<br />\r\n	<span style=\"font-weight: bold;\">Specialties</span>: Financial Regulation, Quantitative Finance, Quantitative Analytics, Market risks, Derivatives, Financial Markets, Financial and Statistical Modeling</p>\r\n<p>\r\n	<a href=\"http://uk.linkedin.com/pub/william-perraudin/7/3b4/777/en\" target=\"_blank\"><img class=\"thumbnails\" src=\"http://local.itvn.fr/test/vnvdm_demo/assets/upload/images/logo/unnamed.png\" style=\"width: 25px; height: 27px;\" /></a></p>\r\n', '', '', '');
