INSERT INTO `glossary` VALUES ('326', 'en', 'Variation', '<p>\r\n	Variation is the difference between the &quot;last&quot; and the settlement price of last trading day</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
