INSERT INTO `glossary` VALUES ('334', 'en', 'Implied Volatility', 'IMPLIED VOLATILITY of an option contract is that value of the volatility of the underlying instrument which, when input in an option pricing model will return a theoretical value equal to the current market price of the option.', '', '                                                                                                                ', '', 'assets/upload/images/no-image.jpg', 'general', '51', '', '1', '0', '2015-05-26', '2015-05-28 14:40:28', '1');
INSERT INTO `glossary` VALUES ('334', 'vn', '', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('335', 'en', 'Futures', '<p>\r\n	In finance, a futures contract (more colloquially, futures) is a standardized contract between two parties to buy or sell a specified asset of standardized quantity and quality for a price agreed upon today (the futures price) with delivery and payment occurring at a specified future date, the delivery date, making it a type of derivative instrument</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('335', 'vn', '', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('336', 'en', 'Call', '<p>\r\n	Call option or &quot;Call&quot; is the right to &quot;call&quot;, or buy, the stock or index to someone else</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('336', 'vn', '', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('337', 'en', 'Put', '<p>\r\n	Put option or &quot;Put&quot; is the right to &quot;put&quot;, or sell, the stock or index to someone else</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('337', 'vn', '', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('338', 'en', 'At the money', '<p>\r\n	A situation where an option&#39;s strike price is identical to the price of the underlying security.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('338', 'vn', '', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('339', 'en', 'In the money', '<p>\r\n	For a call option, when the option&#39;s strike price is below the market price of the underlying asset. For a put option, when the strike price is above the market price of the underlying asset.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('339', 'vn', '', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('340', 'en', 'Out of the money', '<p>\r\n	A call option with a strike price that is higher than the market price of the underlying asset, or a put option with a strike price that is lower than the market price of the underlying asset</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('340', 'vn', '', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('341', 'vn', '', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('341', 'en', 'Quantity of Bid', '<p>\r\n	Quantity of Bid</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '0');
INSERT INTO `glossary` VALUES ('342', 'en', 'Short Put', '<p><strong>A short put involves writing a put option without the reserved cash on hand to purchase the underlying stock.</strong></p><p>The only motive for writing an uncovered put is to earn premium income.</p>', '<p><br></p>', '', '', 'assets/upload/images/550798c414d86.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('344', 'en', 'Limit orders - Daily', '<p>Daily is also known as Good-for-day (GFD). All orders are assumed to be Daily unless otherwise specified. The validity of a Daily order ends at the close of that day\'s Trading Period. Daily  orders entered during the post-trading period of a given trading day will be valid for the following trading day</p>', '<p><br></p>', '', '', null, 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('343', 'en', 'Short call', '<p><strong>This strategy consists of writing an uncovered call option. It profits if the stock price holds steady or declines, and does best if the option expires worthless.</strong></p><p>The only motive for writing an uncovered call option is to earn income from selling premium.</p>', '<p><br></p>', '', '', 'assets/upload/images/55079a2a02bea.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('345', 'en', 'Limit orders - Good till cancelled', '<p>Good till cancelled (GTC) is also known as an open order in some markets. This order remains valid until it is executed, it is cancelled, or the contract expires. All orders are automatically cancelled one year after entry.</p>', '<p><br></p>', '', '', null, 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('346', 'en', 'Limit orders - Good to date', '<p>Good to date (GTD) is also known as an open order in some markets. This order remains valid until it is executed, it is cancelled, the contract expires or until the specified date up to one year from entry on which the order is automatically cancelled.</p>', '<p><br></p>', '', '', null, 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('347', 'en', 'Limit orders - Good to expiry', '<p>Good to expiry is also known as an open order in some markets. This order remains valid until the expiry,it is executed, it is cancelled, or the contract expires.</p>', '<p><br></p>', '', '', null, 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('348', 'en', 'Market orders', '<p>Market orders are not visible in the order book for any market participant and have no specific price limit, but are matched to the best available contra-side bid or offer. For example, a market that is twelve bid and fourteen offered will fill market orders to sell at twelve and market orders to buy at fourteen. Market orders are possible for both futures and options, but are not supported for strategies and futures calendar spreads</p>', '<p><br></p>', '', '', null, 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('349', 'en', 'Limit orders', '<p>Limit orders include a specified price limit, and may not be executed at a price worse than that limit. They are divided into restricted limit orders and unrestricted limit orders.</p>', '<p><br></p>', '', '', null, 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('369', 'en', 'Index', 'An index is a statistical measure of the changes in a portfolio of stocks representing a portion of the overall market.', '', '                                                                                                                ', '', 'assets/upload/images/no-image.jpg', 'general', '51', '', '1', '1171', '2015-05-21', '2015-05-22 09:59:25', '1');
