INSERT INTO `glossary` VALUES ('362', 'en', 'Converse', '<p><strong> A Conversion is an arbitrage strategy in options trading that can be performed for a riskless profit when options are overpriced relative to the underlying stock. To do a conversion, the trader buys the underlying stock and offset it with an equivalent synthetic short stock (long put + short call) position.</strong></p>', null, null, null, null, 'optionstrategies', '50', null, null, null, null, null, '1');
