INSERT INTO `simul_article` VALUES ('en', 'Implied Volatility', null, '1', '<p>In financial mathematics, the implied volatility of an option contract is that value of the volatility of the underlying instrument which, when input in an option pricing model will return a theoretical value equal to the current market price of the option.</p>\n', '', '63', null, '', 'ifrc.fr, vnxindex.com, vnefrc.com, globalwomenceo.com,vndmi.com', '2016-02-29 15:40:10', 'glossary', 'derivatives', null, '1085', '0000-00-00', '0', 'thaovi', '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Implied Volatility Dynamics Among Exchange-Traded Funds and Their Largest Component Stocks', '', '1', '<p>\r\n	<strong>Timothy A. Krause, and Donald Lien </strong></p>\r\n<p>\r\n	The Journal of Derivatives<br><em>Fall 2014, Vol. 22, No. 1: pp. 7-26 </em></p>\r\n<p style=\"text-align: justify;\">\r\n	The authors implement a new model for time-variation in implied volatilities and the correlations among them to look for common factors both at the aggregate market level and also within industries. The securities under consideration are ETFs and the largest of their component stocks.</p>\r\n', '<p style=\"text-align: justify;\">\r\n	Krause and Lien find strong evidence of both market and industry factors in individual stock implied volatilities, with the latter being about one-third the size of the former and mean-reverting more quickly. They then explore how the amount of volatility spillover from the ETF to its components is influenced by option trading volume and the relative importance of the individual stock in the ETF portfolio.</p>\r\n', '64', 'assets/upload/images/logo%20(3).png', 'http://www.iijournals.com/doi/abs/10.3905/jod.2014.22.1.007', 'vndmi.com', null, 'bibliography', 'articles', null, '23147', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'In the money', null, '1', '<p>For a call option, when the option&#39;s strike price is below the market price of the underlying asset. For a put option, when the strike price is above the market price of the underlying asset.</p>\r\n', '', '65', null, null, 'ifrc.fr, vnxindex.com, vnefrc.com, globalwomenceo.com,vndmi.com', '2015-07-30 09:54:39', 'glossary', 'derivatives', null, '1086', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Index', '', '1', 'An index is a statistical measure of the changes in a portfolio of stocks representing a portion of the overall market.', '', '66', null, '', 'vndmi.com,globalwomenceo.com', '2015-05-22 09:59:25', 'glossary', 'derivatives', null, '24369', '2015-05-21', null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Initial Margin', null, '1', '<p>The minimum amount of money that must be in and investment account on the day of a transaction. On futures accounts, the initial margin must be met on any day in which the opening balance starts off below the maintenance margin requirement.</p>\r\n', '', '67', null, null, 'ifrc.fr, vnxindex.com, vnefrc.com, globalwomenceo.com,vndmi.com', '2015-07-30 09:54:39', 'glossary', 'derivatives', null, '1087', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'International Journal of Financial Markets and Derivatives', '', '1', '<p>\r\n	Editor in Chief: Dr. Christos Floros</p>\r\n<p>\r\n	ISSN online: 1756-7149<br>ISSN print: 1756-7130 4 issues per year</p>\r\n<p style=\"text-align: justify;\">\r\n	<em>IJFMD</em> addresses the advancement of contemporary research in the field of financial markets and derivatives. It is an internationally competitive, peer-reviewed journal dedicated to serve as the primary outlet for theoretical and empirical research in all areas of international markets and derivatives.</p>\r\n', '<p><br></p>', '68', 'assets/upload/images/bibliography/review%20%26%20article/International_FFMD(1).jpg', 'http://www.inderscience.com/jhome.php?jcode=ijfmd', 'vndmi.com', null, 'bibliography', 'reviews', null, '23245', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Journal of Derivatives', '', '1', 'Institutional Investor, Inc.\'s Journals are quarterly publications offering in-depth, original and practical research in global investment and finance with an extensive online archive. Each Journal is written and edited by leading practitioners and academics and is read by finance industry professionals.\r\n\r\n	Each Journal provides current analyses and strategic insights that translate into implementable ideas for global investment management **professionals.**', '', '69', 'assets/upload/images/no-image.jpg', 'http://www.iijournals.com/toc/jod/current', 'vndmi.com', '2015-08-31 09:25:09', 'bibliography', 'reviews', null, '23231', '2015-08-31', null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Journal of Derivatives & Hedge Funds', '', '1', '<p style=\"text-align: justify;\">\r\n	<strong>The Journal of Derivatives &amp; Hedge Funds</strong> covers new and emerging trends in derivatives trading, quantitative finance, and hedge funds. It is a unifying forum for the exchange of ideas and information between industry practitioners, academic researchers and money managers from around the world.</p>\r\n', '', '70', 'assets/upload/images/bibliography/review%20%26%20article/Derivativeds_HedgeFunds.jpg', 'http://www.palgrave.com/page/detail/Journal-of-Derivatives-Hedge-Funds/?K=17539641', 'vndmi.com', null, 'bibliography', 'reviews', null, '23243', null, null, null, '1', null);
