INSERT INTO `simul_article` VALUES ('en', 'Last', '', '1', '<p>Last is last traded price</p>\n', '', '72', null, '', 'vndmi.com,globalwomenceo.com', '2016-05-27 10:11:36', 'glossary', 'derivatives', null, '24325', '2015-05-25', '0', 'vanluong', '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Leverage', null, '1', '<p>The magnification of gains and losses by paying only for part of the underlying value of the instrument or asset; the smaller the amount of funds invested, the greater the leverage. It is also known as gearing.</p>\r\n', '', '73', null, null, 'ifrc.fr, vnxindex.com, vnefrc.com, globalwomenceo.com,vndmi.com', '2015-07-30 09:54:39', 'glossary', 'derivatives', null, '1088', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Limit orders', '', '1', '<p>Limit orders include a specified price limit, and may not be executed at a price worse than that limit. They are divided into restricted limit orders and unrestricted limit orders.</p>', '<p><br></p>', '74', null, '', 'vndmi.com,globalwomenceo.com', null, 'glossary', 'derivatives', null, '24349', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Limit orders - Daily', '', '1', '<p>Daily is also known as Good-for-day (GFD). All orders are assumed to be Daily unless otherwise specified. The validity of a Daily order ends at the close of that day&#39;s Trading Period. Daily orders entered during the post-trading period of a given trading day will be valid for the following trading day.</p>\n', '', '75', null, '', 'vndmi.com,globalwomenceo.com', '2016-02-29 15:22:44', 'glossary', 'derivatives', null, '24344', '0000-00-00', '0', 'thaovi', '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Limit orders - Good till cancelled', '', '1', '<p>Good till cancelled (GTC) is also known as an open order in some markets. This order remains valid until it is executed, it is cancelled, or the contract expires. All orders are automatically cancelled one year after entry.</p>', '<p><br></p>', '76', null, '', 'vndmi.com,globalwomenceo.com', null, 'glossary', 'derivatives', null, '24345', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Limit orders - Good to date', '', '1', '<p>Good to date (GTD) is also known as an open order in some markets. This order remains valid until it is executed, it is cancelled, the contract expires or until the specified date up to one year from entry on which the order is automatically cancelled.</p>', '<p><br></p>', '77', null, '', 'vndmi.com,globalwomenceo.com', null, 'glossary', 'derivatives', null, '24346', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Limit orders - Good to expiry', '', '1', '<p>Good to expiry is also known as an open order in some markets. This order remains valid until the expiry,it is executed, it is cancelled, or the contract expires.</p>', '<p><br></p>', '78', null, '', 'vndmi.com,globalwomenceo.com', null, 'glossary', 'derivatives', null, '24347', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('fr', 'Livraison en espèce', null, '1', '', '', '79', null, '', 'ifrc.fr, vnxindex.com, vnefrc.com, globalwomenceo.com,vndmi.com', '2016-02-29 15:25:56', 'glossary', 'derivatives', null, '1074', '0000-00-00', '0', 'thaovi', '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long call', '', '1', '<p>\r\n	<strong>This strategy consists of buying a call option. Buying a call is for investors who want a chance to participate in the underlying stock&#39;s expected appreciation during the term of the option. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The investor buys calls as a way to profit from growth in the underlying stock&#39;s price, without the risk and up-front capital outlay of outright stock ownership. The smaller initial outlay also gives the buyer a chance to achieve greater percentage gains (i.e., greater leverage).</p>\r\n', '', '80', 'assets/upload/images/strategies/Long_Call.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24300', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long Call Butterfly', '', '1', '<p><strong>Combining two short calls at a middle strike, one long call each at a lower and upper strike creates a long call butterfly. </strong></p>\n\n<p style=\"text-align:justify\">The upper and lower strikes (wings) must both be equidistant from the middle strike (body), and all the options must have the same expiration date.This strategy generally profits if the underlying stock is at the body of the butterfly at expiration.</p>\n', '', '81', 'assets/upload/images/54d2fe1cd371e.png', '', 'vndmi.com, globalwomenceo.com', '2016-02-29 15:32:04', 'glossary', 'optionstrategies', null, '24301', '0000-00-00', '0', 'thaovi', '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long Call Calendar Spread', '', '1', '<p>\r\n	<strong>Short one call option and long a second call option with a more distant expiration is an example of a long call calendar spread. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The strategy most commonly involves calls with the same strike (horizontal spread), but can also be done with different strikes (diagonal spread).If the underlying stock remains steady or declines during the life of the near-term option, that option will expire worthless and leave the investor owning the longer-term option free and clear. If both options have the same strike price, the strategy will always require paying a premium to initiate the position.</p>\r\n', '<p><br></p>', '82', 'assets/upload/images/54cb42f4d82c6.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24302', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long Call Condor', '', '1', '<p style=\"text-align: justify;\">\r\n	<strong>A long call condor consists of four different call options of the same expiration. The strategy is constructed of 1 long in-the-money call, 1 short higher middle strike in-the-money call, 1 short middle out-of-money call, 1 long highest strike out-of-money call.&nbsp;</strong></p>\r\n<p>\r\n	This strategy profits if the underlying security is between the two short call strikes at expiration.</p>\r\n', '<p><br></p>', '83', 'assets/upload/images/54cb438db82bc.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24303', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long Condor', '', '1', '<p>\r\n	<strong>A long condor consists of being long one call and short another call with a higher strike, and long one put and short another put with a lower strike.</strong></p>\r\n<p style=\"text-align: justify;\">\r\n	Typically, the call strikes are above and the put strikes below the current level of underlying stock, and the distance between the call strikes equals the distance between the put strikes. All the options must be of the same expiration.</p>\r\n', '<p><br></p>', '84', 'assets/upload/images/54cb4443d8829.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24304', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long Iron Butterfly', '', '1', '<p>\r\n	<strong>This strategy combines a short call at an upper strike, a long call and long put at a middle strike, and short a put at lower strike. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The upper and lower strikes (wings) must both be equidistant from the middle strike (body), and all the options must be the same expiration. This strategy profits if the underlying stock is outside the wings of the iron butterfly at expiration.</p>\r\n', '<p><br></p>', '85', 'assets/upload/images/54cb455a9457d.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24305', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long Put', '', '1', '<p>\r\n	<strong>The investor buys a put contract that is compatible with the expected timing and size of a downturn. </strong></p>\r\n<p>\r\n	Although a put usually doesn’t appreciate $1 for every $1 that the stock declines, the percentage gains can be significant.</p>\r\n', '<p><br></p>', '86', 'assets/upload/images/54cb3fd9133c8.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24290', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long Put Butterfly', '', '1', '<p>\r\n	<strong>A long put butterfly is composed of two short puts at a middle strike, and long one put each at a lower and a higher strike.</strong></p>\r\n<p>\r\n	The upper and lower strikes (wings) must both be equidistant from the middle strike (body), and all the options must be the same expiration.</p>\r\n', '<p><br></p>', '87', 'assets/upload/images/54d2feac55328.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24288', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long Put Calendar Spread (Put Horizontal)', '', '1', '<p>\r\n	<strong>To enter into a long put calendar spread, an investor sells one near-term put option and buys a second put option with a more distant expiration</strong>.</p>\r\n<p>\r\n	The strategy most commonly involves puts with the same strike (horizontal spread), but can also be done with different strikes (diagonal spread).</p>\r\n', '<p><br></p>', '88', 'assets/upload/images/54cb3e2351861.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24287', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long Put Condor', '', '1', '<p>\r\n	<strong>A long put condor consists of four different put options of the same expiration.</strong></p>\r\n<p>\r\n	The strategy is constructed of 1 long out-of-money put at the lowest strike, 1 short out-of-money put at the middle strike, 1 short put at a higher in-the-money strike and 1 long deeper in-the-money put at the highest strike.</p>\r\n', '<p><br></p>', '89', 'assets/upload/images/54cb3dbe00a57.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24286', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long Ratio Call Spread', '', '1', '<p>\r\n	<strong>A long ratio call spread combines one short call and long two calls of the same expiration but with a higher strike. </strong></p>\r\n<p>\r\n	This strategy is essentially a&nbsp;bear call spread&nbsp;and a&nbsp;long call, where the strike of the long call is equal to the upper strike of the bear call spread.a</p>\r\n', '<p><br></p>', '90', 'assets/upload/images/54cb3d1b914b7.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24285', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long Ratio Put Spread', '', '1', '<p>\r\n	<strong>The long ratio put spread is a 1x2 spread combining one short put and two long puts with a lower strike. </strong></p>\r\n<p>\r\n	All options have the same expiration date. This strategy is the combination of a&nbsp;bull put spread&nbsp;and a&nbsp;long put, where the strike of the long put is equal to the lower strike of the bull put spread</p>\r\n', '<p><br></p>', '91', 'assets/upload/images/54cb3c9a8d361.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24284', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long Stock', '', '1', '<p>\r\n	This strategy simply consists of buying shares of the underlying stock. The purchase price sets the cost basis, and the exit price establishes whether there is a net profit or loss on the asset. No gain or loss is final until the stock is actually sold. (However, once received, dividend income of course belongs to the stockowner.)</p>\r\n', '<p><br></p>', '92', 'assets/upload/images/54cb3aff02504.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24282', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long straddle', '', '1', 'A **long straddle** is a combination of buying a call and buying a put, both with the same strike price and expiration. \r\n\r\nTogether, they produce a position that should profit if the stock makes a big move either up or down. Typically, investors buy the straddle because they predict a big price move and/or a great deal of volatility in the near future.', '', '93', null, '', 'vndmi.com, globalwomenceo.com', '2015-08-11 11:01:56', 'glossary', 'optionstrategies', null, '24253', '2015-08-11', null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Long Strangle (Long Combination)', '', '1', '<p>\r\n	<strong>This strategy typically involves buying an out-of-the money call option and an out-of-the-money put option with the same expiration date.</strong></p>\r\n', '<p><br></p>', '94', 'assets/upload/images/54cb3aa996cbd.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24281', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Longevity Risk—A Fine Balance', '', '1', '<p>\r\n	<strong>George Graziani </strong></p>\r\n<p>\r\n	Special Issues<br><em>Fall 2014, Vol. 2014, No. 1: pp. 35-37 </em></p>\r\n<p style=\"text-align: justify;\">\r\n	With longevity now a clearly recognized systematic trend risk, institutions and individuals are looking for guidance in better understanding their exposure. They also seek assistance in developing solutions and products to manage the risk in a way that provides them control and balance with risks and opportunities, including investment, liquidity, and other core business that they face.</p>\r\n', '<p style=\"text-align: justify;\">\r\n	The recognition of financial longevity risk has introduced a new phase where the ultimate holders of this risk are seeking balance. For re-insurers, this means managing the natural offset with their life business now and into the future. For individuals, this means striking a balance between income certainty for life with investment liquidity and control.</p>\r\n', '95', 'assets/upload/images/logo%20(3).png', 'http://www.iijournals.com/doi/abs/10.3905/sp.2014.2014.1.035', 'vndmi.com', null, 'bibliography', 'articles', null, '23144', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Lowest Price', '', '1', '<p>\r\n	Lowest Price</p>\r\n', '', '96', '', '', 'vndmi.com,globalwomenceo.com', null, 'glossary', 'derivatives', null, '24329', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Margin Call', null, '1', '<p>Where the brokerage calls the account holder in order for them to pay more funds into their account to maintain the trade.</p>\r\n', '', '97', null, null, 'ifrc.fr, vnxindex.com, vnefrc.com, globalwomenceo.com,vndmi.com', '2015-07-30 09:54:39', 'glossary', 'derivatives', null, '1089', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Market Maker', null, '1', '<p>A trader or trading firm that buys and sells securities in a market in order to facilitate trading. Market makers make a two side market.</p>\r\n', '', '98', null, null, 'ifrc.fr, vnxindex.com, vnefrc.com, globalwomenceo.com,vndmi.com', '2015-07-30 09:54:39', 'glossary', 'derivatives', null, '1090', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Market orders', '', '1', '<p>Market orders are not visible in the order book for any market participant and have no specific price limit, but are matched to the best available contra-side bid or offer. For example, a market that is twelve bid and fourteen offered will fill market orders to sell at twelve and market orders to buy at fourteen. Market orders are possible for both futures and options, but are not supported for strategies and futures calendar spreads.</p>\n', '', '99', null, '', 'vndmi.com,globalwomenceo.com', '2016-02-29 15:25:21', 'glossary', 'derivatives', null, '24348', '0000-00-00', '0', 'thaovi', '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Naked Call', '', '1', '<p>\r\n	<strong>An investor who writes a call option without owning the underlying stock is banking on a flat to bearish short-term forecast for the stock.</strong></p>\r\n<p>\r\n	The strategy consists of writing the call in hopes that it will lose value through time decay and eventually expire out-of-the-money. If the term ends without the option being assigned, the writer keeps the entire premium initially received, and all obligations under the short call position terminate.</p>\r\n', '<p><br></p>', '100', 'assets/upload/images/54cb39f43dddc.png', '', 'vndmi.com, globalwomenceo.com', null, 'glossary', 'optionstrategies', null, '24280', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'New Mortality Rates Trigger a Leap in Pension Liabilities', '', '1', '<p>\r\n	<strong>Justin W. Owens </strong></p>\r\n<p>\r\n	Special Issues<br><em>Fall 2014, Vol. 2014, No. 1: pp. 17-21 </em></p>\r\n<p style=\"text-align: justify;\">\r\n	The Society of Actuaries (SOA) recently released exposure drafts of new mortality tables that are likely to be used in defined benefit (DB) plan valuations and other pension liability calculations within the next few years.</p>\r\n', '<p style=\"text-align: justify;\">\r\n	These new tables extend the expected life spans of plan participants and therefore increase DB plan liabilities, leading to increased contribution requirements as well as other pension liability–related costs (e.g., lump sum payouts, payments of PBGC variable rate premiums, and so on). Moreover, liability durations will likely increase, which could lead to revisions in liability-driven investing (LDI) strategies. In addition, glide path de-risking schedules may require updates.</p>\r\n', '101', 'assets/upload/images/logo%20(3).png', 'http://www.iijournals.com/doi/abs/10.3905/sp.2014.2014.1.017', 'vndmi.com', null, 'bibliography', 'articles', null, '23142', null, null, null, '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Open Interest', null, '1', '<p>The number of futures or options contracts that have been established and not yet been offset or exercised.</p>\r\n', '', '102', null, null, 'ifrc.fr, vnxindex.com, vnefrc.com, globalwomenceo.com,vndmi.com', '2015-07-30 09:54:39', 'glossary', 'derivatives', null, '1091', null, null, null, '1', null);
