INSERT INTO `article_description` VALUES ('65', 'en', 'Developing derivative securities market and some note for Vietnam', '<p style=\"text-align: justify;\">\r\n	(T&agrave;i ch&iacute;nh) Th trng chng kho&aacute;n (TTCK) ph&aacute;i sinh l&agrave; cp  ph&aacute;t trin cao nht ca TTCK. X&acirc;y dng TTCK ph&aacute;i sinh l&agrave; iu cn thit, l&agrave; xu th ph&aacute;t trin tt yu nhm ho&agrave;n thin th trng vn Vit Nam, qua &oacute; gi&uacute;p y mnh qu&aacute; tr&igrave;nh huy ng v&agrave; khi th&ocirc;ng c&aacute;c ngun vn cho u t ph&aacute;t trin kinh t, vn h&oacute;a, x&atilde; hi.</p>\r\n', '<p style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.2000007629395px; text-align: justify;\">TTCK ph&aacute;i sinh l&agrave; mt nh ch t&agrave;i ch&iacute;nh bc cao chuy&ecirc;n giao dch c&aacute;c chng kho&aacute;n ph&aacute;i sinh, bao gm hp ng k hn (forward contracts), hp ng tng lai (future contracts), hp ng quyn chn (options) v&agrave; ho&aacute;n i (swaps), c ph&aacute;t h&agrave;nh da tr&ecirc;n c s nhng c&ocirc;ng c t&agrave;i ch&iacute;nh &atilde; c&oacute; sn tr&ecirc;n TTCK nh c phiu hay c&aacute;c ch s chng kho&aacute;n. TTCK ph&aacute;i sinh c&oacute; ngun gc t th trng h&agrave;ng h&oacute;a, tuy nhi&ecirc;n, vic x&acirc;y dng v&agrave; ph&aacute;t trin TTCK ph&aacute;i sinh li phc tp hn nhiu th trng h&agrave;ng h&oacute;a do quy m&ocirc; rng ln cng nh t&iacute;nh a dng trong c&aacute;c sn phm ca TTCK ph&aacute;i sinh.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', '', '');
INSERT INTO `article_description` VALUES ('50', 'vn', 'V chung ti', '<p>\r\n	<strong style=\"font-size: 12.222222328186px; text-align: justify;\">Intelligent Financial Research &amp; Consulting (IFRC)&nbsp;</strong><span style=\"font-size: 12.222222328186px; text-align: justify;\">l&agrave; c&ocirc;ng ty a dch v. Sn phm v&agrave; dch v trng yu ca IFRC bao gm Sn phm v&agrave; Dch v t&agrave;i ch&iacute;nh cho Th trng vn, Nghi&ecirc;n cu, &agrave;o to, T vn qun l&yacute; v&agrave; dch v IT. Ch s t&ugrave;y chnh, c x&acirc;y dng bi i ng x&acirc;y dng ch s chuy&ecirc;n nghip, l&agrave; sn phm ch cht ca ch&uacute;ng t&ocirc;i.</span></p>\r\n', '<div style=\"text-align: justify;\">\r\n	<strong>Intelligent Financial Research &amp; Consulting (IFRC)&nbsp;</strong>l&agrave; c&ocirc;ng ty a dch v. Sn phm v&agrave; dch v trng yu ca IFRC bao gm Sn phm v&agrave; Dch v t&agrave;i ch&iacute;nh cho Th trng vn, Nghi&ecirc;n cu, &agrave;o to, T vn qun l&yacute; v&agrave; dch v IT. Ch s t&ugrave;y chnh, c x&acirc;y dng bi i ng x&acirc;y dng ch s chuy&ecirc;n nghip, l&agrave; sn phm ch cht ca ch&uacute;ng t&ocirc;i.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div style=\"text-align: justify;\">\r\n	<strong>Ch&uacute;ng t&ocirc;i cam kt cung cp c&aacute;c dch v v&agrave; s&agrave;n phm tt nht trong sut qua tr&igrave;nh x&acirc;y dng v&agrave; duy tr&igrave; ch s: khai th&aacute;c d liu, thit k, ph&aacute;t trin, c&ocirc;ng b, duy tr&igrave;, h tr, gi&aacute;o dc v&agrave; nghi&ecirc;n cu.</strong></div>\r\n', '', '');
