INSERT INTO `article_description` VALUES ('61', 'en', 'Development derivatives markets and a few notes for Vietnam', '<p style=\"text-align: justify;\">\r\n	(T&agrave;i ch&iacute;nh) Vic x&acirc;y dng th trng chng kho&aacute;n ph&aacute;i sinh (TTCKPS) l&agrave; mt mc ti&ecirc;u quan trng trong chin lc ph&aacute;t trin th trng chng kho&aacute;n Vit Nam 2011-2020 &atilde; c Th tng ph&ecirc; duyt theo quyt nh 252/2012/Q-TTg</p>\r\n', '<p style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.2000007629395px; text-align: justify;\">Mi &acirc;y nht, ng&agrave;y 11/3/2014, Th tng Ch&iacute;nh ph &atilde; k&yacute; Quyt nh s 366/Q-TTg ph&ecirc; duyt  &aacute;n x&acirc;y dng v&agrave; ph&aacute;t trin th trng chng kho&aacute;n ph&aacute;i sinh Vit Nam. &acirc;y c xem l&agrave; nhng bc i u ti&ecirc;n trong vic x&acirc;y dng v&agrave; ph&aacute;t trin TTCKPS ti Vit Nam nhm &aacute;p ng nhu cu cp thit ca c ch ph&ograve;ng v ri ro.</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.2000007629395px; text-align: justify;\">Tuy nhi&ecirc;n, TTCKPS cng c xem l&agrave; mt th trng c&oacute; hiu ng &ograve;n by t&agrave;i ch&iacute;nh cao, do &oacute; vic x&acirc;y dng v&agrave; ph&aacute;t trin th trng n&agrave;y cng ht sc nhy cm, phc tp &ograve;i hi phi c&oacute; s chun b k lng t ph&iacute;a c quan qun l&yacute; nh&agrave; nc cng nh s chun b sn s&agrave;ng ca c&aacute;c th&agrave;nh vi&ecirc;n th trng.</span></p>\r\n', '', '');
INSERT INTO `article_description` VALUES ('60', 'vn', 'Sao phi cn th trng chng khon phi sinh?', '<p>\r\n	(T&agrave;i ch&iacute;nh) n nm 2016, s bt u t chc giao dch c&aacute;c chng kho&aacute;n ph&aacute;i sinh da tr&ecirc;n t&agrave;i sn c s l&agrave; ch s chng kho&aacute;n, tr&aacute;i phiu, c phiu</p>\r\n', '<p>\r\n	Ng&agrave;y 11/3/2014, Th tng Ch&iacute;nh ph Nguyn Tn Dng &atilde; k&yacute; Quyt nh ph&ecirc; duyt  &aacute;n X&acirc;y dng v&agrave; ph&aacute;t trin th trng chng kho&aacute;n (TTCK) ph&aacute;i sinh Vit Nam.  &aacute;n m rng kh&ocirc;ng gian ph&aacute;t trin TTCK Vit Nam ra khi phm vi c&aacute;c sn phm c s v&agrave; x&aacute;c lp l tr&igrave;nh n nm 2016, s bt u t chc giao dch c&aacute;c chng kho&aacute;n ph&aacute;i sinh da tr&ecirc;n t&agrave;i sn c s l&agrave; ch s chng kho&aacute;n, tr&aacute;i phiu, c phiu.</p>\r\n', '', '');
