INSERT INTO `article_description` VALUES ('62', 'en', 'Derivatives securities market has official schedule of development', '<p>\r\n	(T&agrave;i ch&iacute;nh) Ng&agrave;y 11/3/2014, Th tng Ch&iacute;nh ph &atilde; ph&ecirc; duyt  &aacute;n x&acirc;y dng v&agrave; ph&aacute;t trin th trng chng kho&aacute;n (TTCK) ph&aacute;i sinh Vit Nam.</p>\r\n', '<p>\r\n	Kh&ocirc;ng  TTCK ph&aacute;i sinh h&igrave;nh th&agrave;nh v&agrave; hot ng t ph&aacute;t</p>\r\n<p style=\"text-align: justify;\">\r\n	Theo Quyt nh tr&ecirc;n, quan im v&agrave; nguy&ecirc;n tc x&acirc;y dng v&agrave; ph&aacute;t trin TTCK ph&aacute;i sinh c da tr&ecirc;n quan im v&agrave; nguy&ecirc;n tc: Da tr&ecirc;n c&aacute;c ti&ecirc;u chun v&agrave; th&ocirc;ng l quc t, ph&ugrave; hp vi iu kin thc t v&agrave; ph&aacute;t trin kinh t- x&atilde; hi ca t nc; Theo m&ocirc; h&igrave;nh tp trung, hot ng thng nht di s qun l&yacute; ca Nh&agrave; nc, kh&ocirc;ng  TTCK ph&aacute;i sinh h&igrave;nh th&agrave;nh v&agrave; hot ng t ph&aacute;t; La chn v&agrave; chun h&oacute;a c&aacute;c chng kho&aacute;n ph&aacute;i sinh giao dch tr&ecirc;n S giao dch chng kho&aacute;n l&agrave; c&aacute;c hp ng tng lai, hp ng quyn chn da tr&ecirc;n c&aacute;c chng kho&aacute;n c s l&agrave; ch s TTCK, tr&aacute;i phiu ch&iacute;nh ph, c phiu ni&ecirc;m yt.</p>\r\n', '', '');
