INSERT INTO `article_description` VALUES ('66', 'en', 'Experience in building derivatives securities market for Vietnam', '<p style=\"text-align: justify;\">\r\n	(T&agrave;i ch&iacute;nh) Vic nghi&ecirc;n cu kinh nghim s dng c&aacute;c sn phm chng kho&aacute;n ph&aacute;i sinh  c&aacute;c ng&acirc;n h&agrave;ng hay c&aacute;c t chc t&agrave;i ch&iacute;nh ti Australia l&agrave; rt cn thit i vi qu&aacute; tr&igrave;nh n&acirc;ng cao hiu qu qun tr ri ro ti c&aacute;c ng&acirc;n h&agrave;ng thng mi (NHTM) Vit Nam hin nay n&oacute;i ri&ecirc;ng v&agrave; l&agrave; b&agrave;i hc kinh nghim quan trng trong vic x&acirc;y dng th trng chng kho&aacute;n ph&aacute;i sinh  Vit Nam trong thi gian ti.</p>\r\n', '<p style=\"text-align: justify;\">\r\n	T l&acirc;u, c&aacute;c NHTM tr&ecirc;n th gii &atilde; s dng c&aacute;c sn phm chng kho&aacute;n ph&aacute;i sinh nhm n&acirc;ng cao hiu qu qun tr ri ro trong hot ng kinh doanh ng&acirc;n h&agrave;ng. Ti Australia, bn NHTM ln nht nc n&agrave;y l&agrave; Australia and New Zealand Bank Group (ANZ), Commonwealth Bank of Australia (CBA), National Austrlia Bank (NAB) v&agrave; Westpac Banking Corporation (WBK) &atilde; s dng ng&agrave;y c&agrave;ng nhiu c&aacute;c sn phm chng kho&aacute;n ph&aacute;i sinh. Vic l&agrave;m n&agrave;y &atilde; mang li kh&ocirc;ng ch hiu qu gim thiu ri ro m&agrave; c&ograve;n tng th&ecirc;m li nhun cho ng&acirc;n h&agrave;ng.</p>\r\n<p>\r\n	&nbsp;</p>\r\n', '', '');
