INSERT INTO `article_description` VALUES ('68', 'en', 'Development of global derivatives market ', '<p style=\"text-align: justify;\">\r\n	(T&agrave;i ch&iacute;nh) V&agrave;o cui th k 20, tr&agrave;o lu i mi t&agrave;i ch&iacute;nh mnh m &atilde; h&igrave;nh th&agrave;nh n&ecirc;n mt khu vc th trng t&agrave;i ch&iacute;nh vi quy m&ocirc; rng khp tr&ecirc;n to&agrave;n cu, ph&aacute;t trin vi tc  rt cao, &oacute; l&agrave; th trng c&aacute;c c&ocirc;ng c t&agrave;i ch&iacute;nh ph&aacute;i sinh (c&aacute;c hp ng t&agrave;i ch&iacute;nh).</p>\r\n', '<p style=\"text-align: justify;\">\r\n	C&ugrave;ng vi c&aacute;c c&ocirc;ng c t&agrave;i ch&iacute;nh truyn thng (c phiu, tr&aacute;i phiu,...), &atilde; xut hin c&aacute;c c&ocirc;ng c t&agrave;i ch&iacute;nh mi nh hp ng k hn, hp ng tng lai, hp ng quyn chn v&agrave; hp ng ho&aacute;n i. C&aacute;c c&ocirc;ng c t&agrave;i ch&iacute;nh ph&aacute;i sinh da tr&ecirc;n c&aacute;c quan h t&agrave;i ch&iacute;nh c s (t&iacute;n dng, tr&aacute;i phiu, c phiu,...), th&ocirc;ng qua vic s dng c&aacute;c tha thun mi v ni dung, g&oacute;p phn b sung v&agrave; ph&aacute;t trin ch&uacute;ng. Hin nay, vic thit k, a v&agrave;o &aacute;p dng v&agrave; t chc lu th&ocirc;ng c&aacute;c c&ocirc;ng c t&agrave;i ch&iacute;nh ph&aacute;i sinh l&agrave; mt lnh vc c lp trong hot ng t&agrave;i ch&iacute;nh.</p>\r\n', '', '');
