INSERT INTO `article_description` VALUES ('87', 'en', 'I Volatility - Options Calculator', '<p>\r\n	<strong>Basic Calculators </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	Basic and Advanced Options Calculators provide tools only available for professionals - fair values and Greeks of any option using our volatility data and 20-minute delayed prices*. You can customize all the input parameters (option style, price of the underlying instrument, strike, expiration, implied volatility, interest rate and dividends data) or use the IVolatility.com database to populate all those fields for you! The Calculator can also be used to calculate implied volatility for a specific option - the option price is a parameter in this case.</p>\r\n', '', '', '');
