INSERT INTO `article_description` VALUES ('96', 'en', 'Calendar spread', '<p>\r\n	An option transaction consisting of the purchase of an option with a given expiration an the sale of an otherwise identical option with a different expiration.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('96', 'vn', 'Mua v bn song hnh theo lch', '<p style=\"text-align: justify;\">\r\n	S&aacute;ch l&shy;c mua b&aacute;n hp ng options bng c&aacute;ch mua 2 hp ng option c&oacute; c&ugrave;ng loi chng kho&aacute;n nh&shy;ng kh&aacute;c ng&agrave;y &aacute;o hn. Nu gi&aacute; im (exercise price= strike price = gi&aacute; thi h&agrave;nh) ging nhau th&igrave; gi l&agrave; ch&ecirc;nh lch theo chiu ngang (horizontal spread). Nu gi&aacute; thi h&agrave;nh kh&aacute;c nhau th&igrave; gi l&agrave; ch&ecirc;nh lch theo &shy;ng ch&eacute;o (diagonal spread). Nh&agrave; u t&shy; li hay l l&agrave; do ch&ecirc;nh lch gi&aacute; hp hay rng, &iacute;t hay nhiu.</p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('97', 'en', 'Call Option', '<p>\r\n	A call option gives the buyer of the option the right but not the obligation to buy the underlying futures contract at a specified price.</p>\r\n', '', '', '');
