INSERT INTO `article_description` VALUES ('316', 'en', 'Strike price', '<p style=\"text-align:left;\">\r\n	<strong>The strike price is the price at which a specific derivative contract can be exercised, and are fixed in the contract</strong>. For call options, the strike price is where the security can be bought (up to the expiration date), while for put options the strike price is the price at which shares can be sold.</p>\r\n', '', '', '');
