INSERT INTO `article_description` VALUES ('354', 'vn', 'Mai Hu Minh', '<p>\r\n	Gi&aacute;m c iu h&agrave;nh &amp; S&aacute;ng lp vi&ecirc;n</p>\r\n<p>\r\n	<span style=\"font-weight: bold;\">Hc v:</span> Tin s T&agrave;i ch&iacute;nh (i hc Paris Dauphine) v&agrave; Thc s To&aacute;n ng dng (Paris Dauphine - ENSAE)</p>\r\n<p>\r\n	<span style=\"font-weight: bold;\">Kinh nghim:</span> Tr&ecirc;n 15 nm kinh nghim  SGDCK Paris, Euronext ch&acirc;u &Acirc;u v&agrave; New York; ging dy  nhiu trng i hc v&agrave; trng kinh doanh</p>\r\n<p>\r\n	<span style=\"font-weight: bold;\">Chuy&ecirc;n m&ocirc;n:</span> Th trng T&agrave;i ch&iacute;nh, Qun l&yacute; danh mc u t, Sn phm v&agrave; Th trng ph&aacute;i sinh, Thit k Ch s chng kho&aacute;n, T&iacute;nh to&aacute;n v&agrave; Qun l&yacute;, To&aacute;n T&agrave;i ch&iacute;nh, Nghi&ecirc;n cu thc nghim trong t&agrave;i ch&iacute;nh...</p>\r\n<p>\r\n	<a href=\"https://www.linkedin.com/in/huuminhmai\" target=\"_blank\"><img class=\"thumbnails\" src=\"http://local.itvn.fr/test/vnvdm_demo/assets/upload/images/logo/unnamed.png\" style=\"width: 25px; height: 27px;\" /></a>&nbsp;&nbsp;&nbsp; <a href=\"http://local.itvn.fr/vnvdm/assets/upload/PDF/CV_HMM_GB_20150210.pdf\" target=\"_blank\"><img class=\"thumbnails\" src=\"http://local.itvn.fr/test/vnvdm_demo/assets/upload/images/cvl_blue_icon.png\" /></a></p>\r\n', '', '', '');
INSERT INTO `article_description` VALUES ('355', 'en', 'Michel BOKOBZA', '<p>\r\n	<strong>Nationality</strong>: French</p>\r\n<p>\r\n	<span style=\"font-weight: bold;\">Experiences</span>: More than 17 years in the Euronext Liffe, and LCH Clearing House. Lecturer in universities and business schools<br />\r\n	<span style=\"font-weight: bold;\">Specialities</span>: Derivatives Market Operation and Supervision, Clearing Key account management, and Business Development, Customers Services, ...</p>\r\n<p>\r\n	<a href=\"https://www.linkedin.com/in/michelbokobza\" target=\"_blank\"><img class=\"thumbnails\" src=\"http://local.itvn.fr/test/vnvdm_demo/assets/upload/images/logo/unnamed.png\" style=\"width: 25px; height: 27px;\" /></a>&nbsp;&nbsp; <a href=\"http://local.itvn.fr/vnvdm/assets/upload/PDF/CV Michel BOKOBZA UK.pdf\" target=\"_blank\"><img class=\"thumbnails\" src=\"http://local.itvn.fr/test/vnvdm_demo/assets/upload/images/logo/CV.png\" /></a></p>\r\n', '', '', '');
