INSERT INTO `article_description` VALUES ('358', 'en', 'Khuong Duc NGUYEN', '<p>\r\n	<span style=\"font-size:12px;\"><strong>Nationality</strong>: Vietnamese</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Lecturer in Finance, Deputy Director of international cooperation and research department, and Dean of Economics and Finance Faculty, Business Management and Administration Institute (IPAG Business school), Paris, France.</span></strong></p>\r\n<p>\r\n	<span style=\"font-weight: bold;\">Experiences</span>: Delivering modules in international student exchange progammes such as Financial markets, Statistics for Business, Finance in Enterprises, Financial analyses and theory in English; Member of Asian Shadow Financial Regulatory Committee; Participating in writing reports providing Asian governments with consultancy on policies; Co-editor, Associate Editor of the world reputable journals; Reviewer of Scientific studies of 30 international journals; Member of Administrative board or member of Financial and scientific associations in Vietnam and around the world; Organising or member of organising boards of more than 20 international conferences<br />\r\n	<span style=\"font-weight: bold;\">Specialities</span>: Financial Economics in Developing Countries; Corporate Finance; Goods Market; Financial Markets and Monetary System</p>\r\n<p>\r\n	<a href=\"https://www.linkedin.com/pub/duc-khuong-nguyen/31/2a7/5a\" target=\"_blank\"><img class=\"thumbnails\" src=\"http://local.itvn.fr/test/vnvdm_demo/assets/upload/images/logo/unnamed.png\" style=\"width: 25px; height: 27px;\" /></a>&nbsp;&nbsp;&nbsp;<a href=\"http://nguyenduckhuong.com/\" target=\"_blank\"><img class=\"thumbnails\" src=\"http://local.itvn.fr/test/vnvdm_demo/assets/upload/images/logo/www.png\" style=\"width: 25px; height: 27px;\" /></a></p>\r\n', '', '', '');
