INSERT INTO `article_description` VALUES ('359', 'en', 'Eric BERTRAND', '<p>\r\n	<span style=\"font-size:12px;\"><strong>Nationality</strong>: French</span></p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;\">Sales and Marketing Manager, Chief Executive Officer, Chief Financial Analyst for Stock Exchanges and Brokerage Firms</span></strong></p>\r\n<p>\r\n	<span style=\"font-weight: bold;\">Experiences</span>: Sales and Marketing Manager for Paris Stock Exchange, MONEP, Chief Executive Officer of a leading Derivatives Brokerage company in Paris, (ranked N&deg;2 on the MATIF, and N&deg;4 on the MONEP); Chief Financial Analyst and Options Department Manager with the largest French broker, consultant, managers, trainers on a lots of Exchanges from US, Japan, Middle East to Europe.<br />\r\n	<span style=\"font-weight: bold;\">Specialities</span>: Market Knowledge; Derivative Products: Pricing models; Market-Making; Strategic direction and consultant (arbitrage, hedging, active portfolio management); Exchange Business Practices Knowledge</p>\r\n<p>\r\n	<a href=\"https://www.linkedin.com/pub/eric-bertrand/1b/b49/704\" target=\"_blank\"><img class=\"thumbnails\" src=\"http://local.itvn.fr/test/vnvdm_demo/assets/upload/images/logo/unnamed.png\" style=\"width: 25px; height: 27px;\" /></a></p>\r\n', '', '', '');
