INSERT INTO `glossary` VALUES ('93', 'en', 'Arbitrage', '<b>Attempting</b> to profit by exploiting price references of identical or similar commodities or financial instruments on different markets or in different forms. Arbitrage opportunities (If they exist) provide riskless profit opportunities.', '', '                                                                                                                                                                                                                                                          ', '', 'assets/upload/images/no-image.jpg', 'general', '51', '', '1', '0', '2015-05-26', '2015-05-26 15:26:18', '1');
INSERT INTO `glossary` VALUES ('93', 'vn', 'Kinh doanh chnh lch gi', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('96', 'en', 'Calendar spread', '<p>\r\n	An abc option transaction consisting of the purchase of an option with a given expiration an the sale of an otherwise identical option with a different expiration.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('96', 'vn', 'Mua v bn song hnh theo lch', '<p style=\"text-align: justify;\">\r\n	S&aacute;ch l&shy;c mua b&aacute;n hp ng options bng c&aacute;ch mua 2 hp ng option c&oacute; c&ugrave;ng loi chng kho&aacute;n nh&shy;ng kh&aacute;c ng&agrave;y &aacute;o hn. Nu gi&aacute; im (exercise price= strike price = gi&aacute; thi h&agrave;nh) ging nhau th&igrave; gi l&agrave; ch&ecirc;nh lch theo chiu ngang (horizontal spread). Nu gi&aacute; thi h&agrave;nh kh&aacute;c nhau th&igrave; gi l&agrave; ch&ecirc;nh lch theo &shy;ng ch&eacute;o (diagonal spread). Nh&agrave; u t&shy; li hay l l&agrave; do ch&ecirc;nh lch gi&aacute; hp hay rng, &iacute;t hay nhiu.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('97', 'en', 'Call Option', '<p>A <strong>call option </strong>gives the buyer of the option the right but not the obligation to buy the underlying futures contract at a specified price.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('97', 'vn', 'Quyn chn Mua', '<p>\r\n	Mt loi hp ng t&agrave;i ch&iacute;nh din ra gia hai b&ecirc;n, cho ph&eacute;p ngi mua hp ng c&oacute; quyn mua mt s lng x&aacute;c nh c phn ca mt chng kho&aacute;n, ch s chng kho&aacute;n n&agrave;o &oacute;  mt mc gi&aacute; n nh trc, gi l&agrave; &quot;strike price&quot; hoc &quot;exercise price&quot; - v&agrave;o hoc trc ng&agrave;y &aacute;o hn ca hp ng.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('106', 'en', 'Initial Margin', '<p style=\"text-align: justify;\">\r\n	The minimum amount of money that must be in and investment account on the day of a transaction. On futures accounts, the initial margin must be met on any day in which the opening balance starts off below the maintenance margin requirement.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('98', 'en', 'Cash Settlement', '<p>\r\n	Contract that do not permit delivery, rather the contracts are settled at the cash price.</p>\r\n', '<p><br></p>', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('98', 'vn', 'Thanh ton bng tin mt', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('99', 'en', 'Clearing House', '<p>\r\n	The clearinghouse financially guarantees all contracts on the exchange and manages the financial settlement of futures and options contract.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('99', 'vn', 'T chc thanh ton b tr', '<p>\r\n	T chc t&agrave;i ch&iacute;nh m bo cho tt c c&aacute;c hp ng giao dch tr&ecirc;n S giao dch v&agrave; qun l&yacute; thanh to&aacute;n b&ugrave; tr cho c&aacute;c hp ng tng lai v&agrave; quyn chn.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('100', 'en', 'Contract Month / Delivery Month', '<p>\r\n	Indicates the month during which the futures contract expires.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('100', 'vn', 'Hp ng thng/ Giao hng thng', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('101', 'en', 'Contract Specification', '<p style=\"text-align: justify;\">\r\n	A derivative exchange designs its own products and publishes a contract specification setting out the details of the derivative contract. This will include the size or unit of trading and the underlying, maturity months, quotation and minimum price movement and value together with trading times, methods and delivery condition.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('101', 'vn', 'iu khon hp ng', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('102', 'en', 'Counterparty', '<p style=\"text-align: justify;\">\r\n	A trade can take place between two or more counterparties. Usually one party to a trade refers to its trading partners as counterparties.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('102', 'vn', 'Bn i tc', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('103', 'en', 'Delivery Month', '<p>\r\n	Indicates the month during which the futures contract expires.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('103', 'vn', 'Giao hng thng', '<p>\r\n	Giao h&agrave;ng trong sut th&aacute;ng n khi hp ng tng lai ht hn.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('104', 'en', 'Forward Contract', '<p style=\"text-align: justify;\">\r\n	An agreement between two parties, a buyer and seller, to buy an asset or currency at a later date at a fix price.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('104', 'vn', 'Hp ng k hn', '<p style=\"text-align: justify;\">\r\n	Cam kt gia hai b&ecirc;n, b&ecirc;n mua v&agrave; b&ecirc;n b&aacute;n  mua mt t&agrave;i sn hoc ngoi t v&agrave;o mt ng&agrave;y x&aacute;c nh trong tng lai vi mc gi&aacute; c tho thun khi k&yacute; hp ng.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('105', 'en', 'Futures Contract', '<p style=\"text-align: justify;\">\r\n	An agreement between two parties, a buyer and a seller, to purchase an asset or currency at a later date at a fix price and that trades on a futures exchange and is subject to a daily settlement procedure to guarantee to each party that claims against the other parties will be paid.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('105', 'vn', 'Hp ng tng lai', '<p>\r\n	Tho thun gia hai b&ecirc;n ( mua v&agrave; b&aacute;n) mua mt t&agrave;i sn hay ngoi t c chun ho&aacute; ti mc gi&aacute; c x&aacute;c nh ng&agrave;y h&ocirc;m nay v&agrave; giao h&agrave;ng v&agrave;o thi im x&aacute;c nh trong tng lai, giao dch tr&ecirc;n S&agrave;n giao dch tng lai, c m bo thanh to&aacute;n l&atilde;i/ l hng ng&agrave;y tr&ecirc;n t&agrave;i khon k&yacute; qu nhm m bo t&iacute;nh thanh khon cho c&aacute;c b&ecirc;n.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('106', 'vn', 'K qu ban u', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('108', 'en', 'Leverage', '<p style=\"text-align: justify;\">\r\n	The magnification of gains and losses by paying only for part of the underlying value of the instrument or asset; the smaller the amount of funds invested, the greater the leverage. It is also known as gearing.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('108', 'vn', 'n by', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('109', 'en', 'Margin Call', '<p style=\"text-align: justify;\">\r\n	Where the brokerage calls the account holder in order for them to pay more funds into their account to maintain the trade.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('109', 'vn', 'Lnh gi', '<p>\r\n	Trng hp m&ocirc;i gii gi cho ch t&agrave;i khon  h tr tin v&agrave;o t&agrave;i khon ca h  duy tr&igrave; c&aacute;c giao dch.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('110', 'en', 'Market Maker', '<p style=\"text-align: justify;\">\r\n	A trader or trading firm that buys and sells securities in a market in order to facilitate trading. Market makers make a two side market.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('110', 'vn', 'Nh to lp th trng', '<p>\r\n	<span style=\"color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 20px;\">C&aacute;c c&aacute; nh&acirc;n hoc t chc ng ra t giao dch mt hay mt s loi c&ocirc;ng c t&agrave;i ch&iacute;nh. C&aacute;ch h giao dch l&agrave; t m&igrave;nh nh ra mc gi&aacute; mua v&agrave;o v&agrave; b&aacute;n ra ca nhng loi t&agrave;i sn, h&agrave;ng h&oacute;a trong danh mc kinh doanh ca h. Mc ch&ecirc;nh lch gi&aacute; mua gi&aacute; b&aacute;n v&agrave; nhng bin ng gi&aacute;&nbsp;l&agrave; phng tin ch yu  market-maker t&igrave;m kim li nhun.</span></p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('111', 'en', 'Open Interest', '<p>\r\n	The number of futures or options contracts that have been established and not yet been offset or exercised.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('111', 'vn', 'Ct ang giao dch', '<p>\r\n	Th hin tng s lng hp ng tng lai hoc quyn chn ang giao dch.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('112', 'en', 'Position Limit', '<p>\r\n	The maximum number of options or futures contracts that any one investor can hold.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('112', 'vn', 'Hn mc v th', '<p>\r\n	S lng hp ng tng lai v&agrave; quyn chn ti a m&agrave; nh&agrave; u t ang nm gi.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('113', 'en', 'Settlement price', '<p style=\"text-align:left;\">The official price established by the clearinghouse at the end of each day for use in the daily settlement.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('113', 'vn', 'Gi thanh ton', '<p>\r\n	Gi&aacute; ch&iacute;nh thc c t chc thanh to&aacute;n b&ugrave; tr thit lp v&agrave;o cui mi ng&agrave;y cho vic thanh to&aacute;n hng ng&agrave;y.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('114', 'en', 'Short Position', '<p>\r\n	A term used to refer to holding a short position or to the party holding the short position.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('114', 'vn', 'V th on', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('115', 'en', 'Spread', '<p>\r\n	A derivatives transaction consisting of a long position in one contract and a short position in another, similar contract.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('115', 'vn', '', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('116', 'en', 'Tick Size', '<p>\r\n	The minimum permissible price fluctuation.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('116', 'vn', '', '<p>\r\n	S bin ng gi&aacute; cho ph&eacute;p ti thiu.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('117', 'en', 'Underlying Asset', '<p>\r\n	The asset or instrument on which a derivative&rsquo;s payoff is based.&nbsp;</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('117', 'vn', 'Ti sn c s', '<p>\r\n	Loi t&agrave;i sn s c giao tr&ecirc;n c s mua hoc b&aacute;n khi quyn chn c thc hin.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('253', 'en', 'Long straddle', '<p>\r\n	<strong>A long straddle is a combination of buying a call and buying a put, both with the same strike price and expiration. </strong></p>\r\n<p>\r\n	Together, they produce a position that should profit if the stock makes a big move either up or down. Typically, investors buy the straddle because they predict a big price move and/or a great deal of volatility in the near future.</p>\r\n', '', '', '', 'assets/upload/images/strategies/long%20straddle.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('253', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/long%20straddle.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('259', 'en', 'Synthetic Short Stock', '<p>\r\n	<strong>The strategy combines two option positions: short a call option and long a put option with the same strike and expiration. </strong></p>\r\n<p>\r\n	The net result simulates a comparableshort stockposition\'s risk and reward. The principal differences are the time limitation imposed by the term of the options, the absence of the large initial cash inflow that a short sale would produce, but also the absence of the practical difficulties and obligations associated with short sales.</p>\r\n', '', '', '', 'assets/upload/images/strategies/Synthetic%20Short%20Stock.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('259', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Synthetic%20Short%20Stock.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('260', 'en', 'Synthetic Long Stock', '<p>\r\n	<strong>The strategy combines two option positions: long a call option and short a put option with the same strike and expiration. </strong></p>\r\n<p>\r\n	The net result simulates a comparable long stock position&#39;s risk and reward. The principal differences are the smaller capital outlay, the time limitation imposed by the term of the options, and the absence of a stock owner&#39;s rights: voting and dividends.</p>\r\n', '', '', '', 'assets/upload/images/strategies/Synthetic%20Long%20Stock.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('260', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Synthetic%20Long%20Stock.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('261', 'en', 'Synthetic Long Put', '<p>\r\n	<strong>By combining a&nbsp;long call&nbsp;option and a&nbsp;short stock&nbsp;position, the investor simulates a&nbsp;long put&nbsp;position. </strong></p>\r\n<p>\r\n	The object is to see the combined position gain value as the result of a predicted decline in the underlying stock\'s price.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb367656e8f.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('261', 'vn', '', '', '', '', '', '', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('262', 'en', 'Short Ratio Put Spread', '<p>\r\n	<strong>The short ratio put spread involves buying one put (generally at-the-money) and selling two puts of the same expiration but with a lower strike. </strong></p>\r\n<p>\r\n	This strategy is the combination of a&nbsp;bear put spread&nbsp;and a&nbsp;naked put, where the strike of the naked put is equal to the lower strike of the bear put spread.</p>\r\n', '', '', '', 'assets/upload/images/strategies/Short%20Ratio%20Put%20Spread.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('262', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Short%20Ratio%20Put%20Spread.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('263', 'en', 'Short Ratio Call Spread', '<p>\r\n	<strong>A short call ratio spread means buying one call (generally an at-the-money call) and selling two calls at the same expiration but with a higher strike. </strong></p>\r\n<p>\r\n	This strategy is the combination of a&nbsp;bull call spread&nbsp;and a&nbsp;naked call, where the strike of the naked call is equal to the upper strike of the bull call spread.</p>\r\n', '', '', '', 'assets/upload/images/strategies/Short%20Ratio%20Call%20Spread.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('263', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Short%20Ratio%20Call%20Spread.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('264', 'en', 'Short Strangle', '<p>\r\n	<strong>Selling a call and selling a put with the same expiration, but where the call strike price is above the put strike price is known as the short strangle strategy. </strong></p>\r\n<p>\r\n	Typically both options are out-of-the-money when the strategy is initiated.</p>\r\n', '', '', '', 'assets/upload/images/strategies/Short%20Straddle.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('264', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Short%20Straddle.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('265', 'en', 'Bear Call Spread', '<p>\r\n	<strong>A bear call spread contains two calls with the same expiration but different strikes. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The strike price of the short call is below the strike of the long call, which means this strategy will always generate a net cash inflow (net credit) at the outset.</p>\r\n', '', '', '', 'assets/upload/images/strategies/bear%20call%20spr.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('265', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/bear%20call%20spr.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('266', 'en', 'Short Straddle', '<p>\r\n	<strong>A short straddle is a combination of writing uncovered calls (bearish) and writing uncovered puts (bullish), both with the same strike price and expiration. </strong></p>\r\n<p>\r\n	Together, they produce a position that predicts a narrow trading range for the underlying stock.</p>\r\n', '', '', '', 'assets/upload/images/strategies/Short%20Straddle.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('266', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Short%20Straddle.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('267', 'en', 'Short Stock', '<p>\r\n	<strong>Selling stock short means borrowing stock through the brokerage firm and selling it at the current market price, which the short seller believes is due for a downturn. </strong></p>\r\n<p>\r\n	The plan is to buy the borrowed stock back later for less, allowing the investor to keep the difference between the two prices.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb4a439e7ac.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('267', 'vn', '', '', '', '', '', '', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('268', 'en', 'Bear Put Spread', '<p>\r\n	<strong>A bear put spread contains two puts with the same expiration but different strikes. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The strike price of the short put is below the strike of the long put. Because of the way the strike prices are selected, this strategy requires a net cash outlay (net debit) at the outset.</p>\r\n', '', '', '', 'assets/upload/images/strategies/BEAR%20PUT%20SPR.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('268', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/BEAR%20PUT%20SPR.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('269', 'en', 'Short Put Calendar Spread (Short Put Time Spread)', '<p>\r\n	<strong>Buying one put option and selling a second put option with a more distant expiration is an example of a short put calendar spread.</strong></p>\r\n<p>\r\n	The strategy most commonly involves puts with the same strike (horizontal spread) but can also be done with different strikes (diagonal spread)</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb37138f21e.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('269', 'vn', '', '', '', '', '', '', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('270', 'en', 'Short Put Butterfly', '<p>\r\n	<strong>Buying two puts at a middle strike, and selling one put each at a lower and upper strike results in a short put butterfly. </strong></p>\r\n<p>\r\n	The upper and lower strikes (wings) must both be equidistant from the middle strike (body), and all the options must be the same expiration</p>\r\n', '', '', '', 'assets/upload/images/strategies/Short%20Put%20Butterfly.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('270', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Short%20Put%20Butterfly.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('271', 'en', 'Bear Spread Spread', '<p>\r\n	<strong>This strategy is the combination of a bear call spread and a bear put spread. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	This strategy consists of being short one call and long another call with a higher strike; also long one put and short another put with a lower strike</p>\r\n', '', '', '', 'assets/upload/images/strategies/bear%20spr%20spr.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('271', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/bear%20spr%20spr.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('272', 'en', 'Short Iron Butterfly', '<p>\r\n	<strong>A short iron butterfly consists of being long a call at an upper strike, short a call and short a put at a middle strike, and long a put at a lower strike.</strong></p>\r\n<p>\r\n	The upper and lower strikes (wings) must both be equidistant from the middle strike (body), and all the options must be the same expiration. An alternative way to think about this strategy is a&nbsp;short straddle&nbsp;surrounded by a&nbsp;long strangle.&nbsp; It could also be considered as a&nbsp;bear call spread&nbsp;and a&nbsp;bull put spread.</p>\r\n', '', '', '', 'assets/upload/images/strategies/Short%20Iron%20Butterfly.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('272', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Short%20Iron%20Butterfly.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('273', 'en', 'Bull Call Spread', '<p>\r\n	<strong>A bull call spread contains two calls with the same expiration but different strikes. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The strike price of the short call is higher than the strike of the long call, which means this strategy will always require an initial outlay (debit). The short call&#39;s main purpose is to help pay for the long call&#39;s upfront cost.</p>\r\n', '', '', '', 'assets/upload/images/strategies/bull%20call%20spr.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('273', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/bull%20call%20spr.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('274', 'en', 'Short Condor (Iron Condor)', '<p>\r\n	<strong>To construct a short condor, the investor sells one call while buying another call with a higher strike and sells one put while buying another put with a lower strike.</strong></p>\r\n<p>\r\n	Typically, the call strikes are above and the put strikes below the current level of underlying stock, and the distance between the call strikes equals the distance between the put strikes. All the options must be of the same expiration.</p>\r\n', '', '', '', 'assets/upload/images/strategies/Short%20condor.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('274', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Short%20condor.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('275', 'en', 'Short Call Calendar Spread (Short Call Time Spread)', '<p>\r\n	<strong>Selling a call calendar spread consists of buying one call option and selling a second call option with a more distant expiration. </strong></p>\r\n<p>\r\n	The strategy most commonly involves calls with the same strike (horizontal spread), but can also be done with different strikes (diagonal spread).</p>\r\n', '', '', '', 'assets/upload/images/strategies/Short%20Call%20Calendar%20Spread.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('275', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Short%20Call%20Calendar%20Spread.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('276', 'en', 'Short Call Butterfly', '<p>\r\n	<strong>A short call butterfly consists of two long calls at a middle strike and short one call each at a lower and upper strike</strong>.</p>\r\n<p>\r\n	The upper and lower strikes (wings) must both be equidistant from the middle strike (body), and all the options must have the same expiration date.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54d2fcd7322e2.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('276', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Short%20Call%20Butterfly.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('277', 'en', 'Bull Put Spread', '<p>\r\n	<strong>A bull put spread involves being short a put option and long another put option with the same expiration but with a lower strike. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The strike price of the short put is higher than the strike of the long put. Because of the relationship between the two strike prices, the investor will always receive a premium (credit) when initiating this position.</p>\r\n', '', '', '', 'assets/upload/images/strategies/bull%20put%20spr.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('277', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/bull%20put%20spr.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('278', 'en', 'Protective Put (Married Put)', '<p>\r\n	<strong>A&nbsp;long put&nbsp;option added to&nbsp;long stockinsures the stock\'s value. The choice of strike prices determines where the downside protection \'kicks in. </strong></p>\r\n<p>\r\n	If the stock stays strong, the investor still gets the benefit of upside gains. (In fact, if the short-term forecast brightens before the put expires, it could be sold back to recoup some of its cost.) However, if the stock falls below the strike, as originally feared, the investor has the benefit of several choices.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb3984854d5.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('278', 'vn', '', '', '', '', '', '', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('279', 'en', 'Bull Spread Spread', '<p>\r\n	<strong>This strategy consists of being long one call and short another call with a higher strike, and short one put with a long put on a lower strike. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	Typically, the call strikes are above and the put strikes below the current level of underlying stock, and the distance between the call strikes equals the distance between the put strikes. All options must have the same expiration date.</p>\r\n', '', '', '', 'assets/upload/images/strategies/BULL%20SPR%20SPR.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('279', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/BULL%20SPR%20SPR.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('280', 'en', 'Naked Call', '<p>\r\n	<strong>An investor who writes a call option without owning the underlying stock is banking on a flat to bearish short-term forecast for the stock.</strong></p>\r\n<p>\r\n	The strategy consists of writing the call in hopes that it will lose value through time decay and eventually expire out-of-the-money. If the term ends without the option being assigned, the writer keeps the entire premium initially received, and all obligations under the short call position terminate.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb39f43dddc.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('280', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Naked%20Call.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('281', 'en', 'Long Strangle (Long Combination)', '<p>\r\n	<strong>This strategy typically involves buying an out-of-the money call option and an out-of-the-money put option with the same expiration date.</strong></p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb3aa996cbd.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('281', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Long%20Strangle%20(Long%20Combination).png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('282', 'en', 'Long Stock', '<p>\r\n	This strategy simply consists of buying shares of the underlying stock. The purchase price sets the cost basis, and the exit price establishes whether there is a net profit or loss on the asset. No gain or loss is final until the stock is actually sold. (However, once received, dividend income of course belongs to the stockowner.)</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb3aff02504.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('282', 'vn', '', '', '', '', '', '', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('283', 'en', 'Cash-Backed Call', '<p>\r\n	<strong>The investor buys a call option, and sets aside in a risk-free interest-bearing instrument enough cash to exercise it. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The call guarantees a maximum purchase price during the life of the option, while leaving the investor free to take advantage of any downturn that might occur in the stock price.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb3c0d58215.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('283', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Cash-Backed%20Call.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('284', 'en', 'Long Ratio Put Spread', '<p>\r\n	<strong>The long ratio put spread is a 1x2 spread combining one short put and two long puts with a lower strike. </strong></p>\r\n<p>\r\n	All options have the same expiration date. This strategy is the combination of a&nbsp;bull put spread&nbsp;and a&nbsp;long put, where the strike of the long put is equal to the lower strike of the bull put spread</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb3c9a8d361.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('284', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Long%20Ratio%20Put%20Spread.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('285', 'en', 'Long Ratio Call Spread', '<p>\r\n	<strong>A long ratio call spread combines one short call and long two calls of the same expiration but with a higher strike. </strong></p>\r\n<p>\r\n	This strategy is essentially a&nbsp;bear call spread&nbsp;and a&nbsp;long call, where the strike of the long call is equal to the upper strike of the bear call spread.a</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb3d1b914b7.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('285', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Long%20Ratio%20Call%20Spread.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('286', 'en', 'Long Put Condor', '<p>\r\n	<strong>A long put condor consists of four different put options of the same expiration.</strong></p>\r\n<p>\r\n	The strategy is constructed of 1 long out-of-money put at the lowest strike, 1 short out-of-money put at the middle strike, 1 short put at a higher in-the-money strike and 1 long deeper in-the-money put at the highest strike.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb3dbe00a57.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('286', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Long%20Put%20Condor.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('287', 'en', 'Long Put Calendar Spread (Put Horizontal)', '<p>\r\n	<strong>To enter into a long put calendar spread, an investor sells one near-term put option and buys a second put option with a more distant expiration</strong>.</p>\r\n<p>\r\n	The strategy most commonly involves puts with the same strike (horizontal spread), but can also be done with different strikes (diagonal spread).</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb3e2351861.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('287', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Long%20Put%20Calendar%20Spread%20(Put%20Horizontal).png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('288', 'en', 'Long Put Butterfly', '<p>\r\n	<strong>A long put butterfly is composed of two short puts at a middle strike, and long one put each at a lower and a higher strike.</strong></p>\r\n<p>\r\n	The upper and lower strikes (wings) must both be equidistant from the middle strike (body), and all the options must be the same expiration.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54d2feac55328.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('288', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Long%20Put%20Butterfly.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('289', 'en', 'Cash-Secured Put', '<p>\r\n	<strong>The cash-secured put involves writing an at-the-money or out-of-the-money put option and simultaneously setting aside enough cash to buy the stock. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The goal is to be assigned and acquire the stock below today\'s market price. Whether or not the put is assigned, all outcomes are presumably acceptable. The premium income will help the net results in any event.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb3f872681d.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('289', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Cash-Secured%20Put.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('290', 'en', 'Long Put', '<p>\r\n	<strong>The investor buys a put contract that is compatible with the expected timing and size of a downturn. </strong></p>\r\n<p>\r\n	Although a put usually doesnt appreciate $1 for every $1 that the stock declines, the percentage gains can be significant.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb3fd9133c8.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('290', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Long%20Put.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('295', 'en', 'Collar', '<p>\r\n	<strong>An investor writes a call option and buys a put option with the same expiration as a means to hedge a long position in the underlying stock. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The investor will select a call strike above and a long put strike below the starting stock price.There is latitude, but the strike choices will affect the cost of the hedge as well as the protection it provides. These strikes are referred to as the \'floor\' and the \'ceiling\' of the position, and the stock is \'collared\' between the two strikes.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb404db68c7.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('295', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Collar.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('296', 'en', 'Covered Call', '<p>\r\n	<strong>An investor who buys or owns stock and writes call options in the equivalent amount can earn premium income without taking on additional risk. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The premium received adds to the investor\'s bottom line regardless of outcome. It offers a small downside \'cushion\' in the event the stock slides downward and can boost returns on the upside.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb4087506c0.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('296', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Covered%20Call.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('297', 'en', 'Covered Put', '<p>\r\n	<strong>The idea is to sell the stock short and sell a deep-in-the-money put that is trading for close to its intrinsic value.&nbsp;</strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The profit would then be the interest earned on what is essentially a zero outlay. The danger is that the stock rallies above the strike price of the put, in which case the risk is open-ended.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb40ff0bfc1.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('297', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Covered%20Put.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('298', 'en', 'Covered Ratio Spread', '<p>\r\n	<strong>This strategy consists of being long stock, short two calls at one strike and long a call at a higher strike. All the options must have the same expiration date. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	This strategy profits if the underlying stock moves up to, but not above, the strike price of the short calls. Beyond that, the profit erodes and then hits a plateau.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54d979dc8bc3c.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('298', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Covered%20Ratio%20Spread.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('299', 'en', 'Covered Strangle', '<p>\r\n	<strong>This strategy consists of two parts: (1) short a call and long the underlying stock, and (2) short a put with sufficient cash to purchase the stock if assigned. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	If the stock rises above the call strike at expiration, the investor is most likely assigned on the call, which means selling their stock at the call strike. If the stock falls below the put strike at expiration, the investor is more than likely assigned on the put and obligated to buy more stock at the put strike.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb41cce189a.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('299', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Covered%20Strangle.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('300', 'en', 'Long call', '<p>\r\n	<strong>This strategy consists of buying a call option. Buying a call is for investors who want a chance to participate in the underlying stock&#39;s expected appreciation during the term of the option. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The investor buys calls as a way to profit from growth in the underlying stock&#39;s price, without the risk and up-front capital outlay of outright stock ownership. The smaller initial outlay also gives the buyer a chance to achieve greater percentage gains (i.e., greater leverage).</p>\r\n', '', '', '', 'assets/upload/images/strategies/Long_Call.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('300', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Long_Call.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('301', 'en', 'Long Call Butterfly', '<p>\r\n	<strong>Combining two short calls at a middle strike, and one long call each at a lower and upper strike creates a long call butterfly. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The upper and lower strikes (wings) must both be equidistant from the middle strike (body), and all the options must have the same expiration date.This strategy generally profits if the underlying stock is at the body of the butterfly at expiration.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54d2fe1cd371e.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('301', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Long%20Call%20Butterfly.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('302', 'en', 'Long Call Calendar Spread', '<p>\r\n	<strong>Short one call option and long a second call option with a more distant expiration is an example of a long call calendar spread. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The strategy most commonly involves calls with the same strike (horizontal spread), but can also be done with different strikes (diagonal spread).If the underlying stock remains steady or declines during the life of the near-term option, that option will expire worthless and leave the investor owning the longer-term option free and clear. If both options have the same strike price, the strategy will always require paying a premium to initiate the position.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb42f4d82c6.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('302', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Long%20Call%20Calendar%20Spread.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('303', 'en', 'Long Call Condor', '<p style=\"text-align: justify;\">\r\n	<strong>A long call condor consists of four different call options of the same expiration. The strategy is constructed of 1 long in-the-money call, 1 short higher middle strike in-the-money call, 1 short middle out-of-money call, 1 long highest strike out-of-money call.&nbsp;</strong></p>\r\n<p>\r\n	This strategy profits if the underlying security is between the two short call strikes at expiration.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb438db82bc.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('303', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Long%20Call%20Condor.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('304', 'en', 'Long Condor', '<p>\r\n	<strong>A long condor consists of being long one call and short another call with a higher strike, and long one put and short another put with a lower strike.</strong></p>\r\n<p style=\"text-align: justify;\">\r\n	Typically, the call strikes are above and the put strikes below the current level of underlying stock, and the distance between the call strikes equals the distance between the put strikes. All the options must be of the same expiration.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb4443d8829.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('304', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Long%20Condor.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('305', 'en', 'Long Iron Butterfly', '<p>\r\n	<strong>This strategy combines a short call at an upper strike, a long call and long put at a middle strike, and short a put at lower strike. </strong></p>\r\n<p style=\"text-align: justify;\">\r\n	The upper and lower strikes (wings) must both be equidistant from the middle strike (body), and all the options must be the same expiration. This strategy profits if the underlying stock is outside the wings of the iron butterfly at expiration.</p>\r\n', '<p><br></p>', '', '', 'assets/upload/images/54cb455a9457d.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('305', 'vn', '', '', '', '', '', 'assets/upload/images/strategies/Long%20Iron%20Butterfly.png', 'optionstrategies', '50', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('315', 'en', 'Expiry', '<p>\r\n	<strong>Expiry or Expiry Date</strong> is last day that an options or futures contract is valid.</p>\r\n', '<p>\r\n	test</p>\r\n', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('314', 'en', 'Volatility surface', '<p>\r\n	<strong>Volatility is a measure of the uncertainty regarding the magnitude of change in the future value of a financial instrument. </strong></p>\r\n<p>\r\n	It is a key parameter in the pricing of options. All other things being equal, high volatility implies a higher option price and low volatility a lower price. Basic option pricing models often assume that volatility is constant and independent of both an option&rsquo;s strike price and its time-to-expiry. In practice this rarely occurs so, for correct option valuation, we need to capture the interrelationship between volatility, strike price and time-to-expiry through a volatility smile or a volatility surface.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('314', 'vn', '', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('315', 'vn', 'Expiry', null, '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('316', 'en', 'Strike price', '<p style=\"text-align:left;\">\r\n	<strong>The strike price is the price at which a specific derivative contract can be exercised, and are fixed in the contract</strong>. For call options, the strike price is where the security can be bought (up to the expiration date), while for put options the strike price is the price at which shares can be sold.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('316', 'vn', '', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('317', 'en', 'Bid price', '<p>\r\n	The bid price is the price a buyer is willing to pay for a security.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('317', 'vn', '', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('318', 'en', 'Ask price', '<p>\r\n	The ask price or offer price is the price a seller is willing to accept for a security.</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('318', 'vn', '', '', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
INSERT INTO `glossary` VALUES ('319', 'en', 'Bid Ask Spread', '<p style=\"text-align:left\">\r\n	The <strong>Bid Ask Spread</strong> is the difference in price between the highest price that a buyer is willing to pay (Best Ask) for an asset and the lowest price for which a seller is willing to sell it (Best Bid).</p>\r\n', '', '', '', '', 'general', '51', '', null, null, null, null, '1');
