INSERT INTO `simul_article` VALUES ('en', 'Protective Put (Married Put)', '', '1', '<p>A&nbsp;long put&nbsp;option added to&nbsp;long stockinsures the stock&#39;s value. The choice of strike prices determines where the downside protection &#39;kicks in&rsquo;.</p>\n\n<p>If the stock stays strong, the investor still gets the benefit of upside gains. (In fact, if the short-term forecast brightens before the put expires, it could be sold back to recoup some of its cost.) However, if the stock falls below the strike, as originally feared, the investor has the benefit of several choices.</p>\n', '', '120', 'assets/upload/images/54cb3984854d5.png', '', 'vndmi.com, globalwomenceo.com', '2016-02-26 08:47:36', 'glossary', 'optionstrategies', null, '24278', '0000-00-00', '0', 'Xun Hng', '1', null);
INSERT INTO `simul_article` VALUES ('en', 'Put', null, '1', '<p>Put option or &quot;Put&quot; is the right to &quot;put&quot;, or sell, the stock or index to someone else.</p>\n', '', '121', null, '', 'ifrc.fr, vnxindex.com, vnefrc.com, globalwomenceo.com,vndmi.com', '2016-02-29 15:38:32', 'glossary', 'derivatives', null, '1094', '0000-00-00', '0', 'thaovi', '1', null);
