INSERT INTO `vdm_strategy_description` VALUES ('27', 'EN', 'long_iron_butterfly', 'Long Iron Butterfly', ' Direction neutral/volatility bullish. Holder expects a market move in either direction. The position will also benefit from an increase in volatility', 'Limited; maximised where the underlying rises to strike C or falls to strike A', 'Limited to the net debit in establishing the position, greatest if underlying is at B. ', ' Reached when underlying is above or below strike price B by the same amount as the initial debit.', 'Sell a Put OTM 95,  Buy a Put or a Call ATM 100, Sell a Call OTM 105', 'options');
INSERT INTO `vdm_strategy_description` VALUES ('28', 'EN', 'reverse_iron_butterfly', 'Reverse Iron Butterfly', null, null, null, null, 'Sell a Put OTM 95,  Buy a Put and a Call ATM 100, Sell a Call OTM 105', 'options');
