INSERT INTO `vdm_strategy_description` VALUES ('30', 'EN', 'long_call_butterfly', 'Long Call Butterfly', 'Direction neutral/volatility bearish. If the underlying is at, or about strike B and is expected to remain at this level, or it is felt that volatility will fall.', ' Limited to the net credit in establishing the position. Maximised when the underlying is at B.', 'Limited loss occurs if there is a directional move in the market. Maximised at the lower strike A, and the higher strike C. ', 'Reached when underlying is above or below strike price B by the same amount as the net credit in establishing the position.', 'Buy a Put OTM 95, Sell Put and Call ATM 100,  Buy a Call OTM 105', 'options');
INSERT INTO `vdm_strategy_description` VALUES ('31', 'EN', 'long_put_butterfly', 'Long Put Butterfly', 'Direction neutral/volatility bearish. If the underlying is at, or about strike B and is expected to remain at this level, or it is felt that volatility will fall.', ' Limited to the net credit in establishing the position. Maximised when the underlying is at B. ', ' Limited loss occurs if there is a directional move in the market. Maximised at the lower strike A, and the higher strike C. ', ' Reached when underlying is above or below strike price B by the same amount as the net credit in establishing the position.', 'Buy a Put OTM 95, Sell Put and Call ATM 100, Buy a Call OTM 105', 'options');
INSERT INTO `vdm_strategy_description` VALUES ('32', 'EN', 'short_call_butterfly', 'Short Call Butterfly', 'Direction neutral/volatility bullish. Holder expects a market move in either direction. The position will also benefit from an increase in volatility', 'Limited; maximised where the underlying rises to strike C or falls to strike A', 'Limited to the net debit in establishing the position, greatest if underlying is at B. ', 'Reached when underlying is above or below strike price B by the same amount as the initial debit', 'Sell a Put OTM 95,  Buy a Put and a Call ATM 100, Sell a Call OTM 105', 'options');
INSERT INTO `vdm_strategy_description` VALUES ('33', 'EN', 'short_put_butterfly', 'Short Put Butterfly', 'Direction neutral/volatility bullish. Holder expects a market move in either direction. The position will also benefit from an increase in volatility', 'Limited; maximised where the underlying rises to strike C or falls to strike A', 'Limited to the net debit in establishing the position, greatest if underlying is at B', 'Reached when underlying is above or below strike price B by the same amount as the initial debit', 'Sell a Put OTM 95,  Buy a Put and a Call ATM 100, Sell a Call OTM 105', 'options');
